// TradeSum.js

import React, { useState, useEffect } from "react";
import { fetchTrades } from "./ApiCalls";

const TradeSum = () => {
  // eslint-disable-next-line
  const [dailyTrades, setDailyTrades] = useState([]);
  const [weeklyTrades, setWeeklyTrades] = useState([]);
  const [monthlyTrades, setMonthlyTrades] = useState([]);

  useEffect(() => {
    const fetchAndCategorizeTrades = async () => {
      try {
        const trades = await fetchTrades();
        const today = new Date();
        const startOfWeek = new Date(today);
        startOfWeek.setDate(today.getDate() - today.getDay() + 1); // Monday

        const daily = [];
        const weekly = [];
        const monthly = [];

        trades.forEach((trade) => {
          const tradeDate = new Date(trade.entryDate);
          if (tradeDate.toDateString() === today.toDateString()) {
            daily.push(trade);
          }
          if (tradeDate >= startOfWeek && tradeDate <= today) {
            weekly.push(trade);
          }
          if (
            tradeDate.getMonth() === today.getMonth() &&
            tradeDate.getFullYear() === today.getFullYear()
          ) {
            monthly.push(trade);
          }
        });

        setDailyTrades(daily);
        setWeeklyTrades(weekly);
        setMonthlyTrades(monthly);
      } catch (error) {
        console.error("Error fetching trades:", error);
      }
    };

    fetchAndCategorizeTrades();
  }, []);

  const renderTrades = (trades) => {
    const totalTrades = trades.length;
    const winningTrades = trades.filter((trade) => trade.profitLoss > 0).length;
    const winRate =
      totalTrades > 0 ? ((winningTrades / totalTrades) * 100).toFixed(2) : "0";

    return (
      <div>
        {trades.map((trade) => (
          <div key={trade.id} className="trade-item">
            <div className="left-column">
              {trade.symbol} {trade.optionType} ${trade.strikePrice}{" "}
              {trade.expDate.split("-").slice(1).join("/")}
            </div>
            <div className="middle-column">
              ${trade.entryPrice.toFixed(2)} - ${trade.exitPrice.toFixed(2)}
            </div>
            <div className="right-column">
              {(
                ((trade.exitPrice - trade.entryPrice) / trade.entryPrice) *
                100
              ).toFixed(2)}
              %
            </div>
          </div>
        ))}
        <div className="win-rate">Win Rate: {winRate}%</div>
        <div className="win-rate">P/L: {winningTrades}%</div>
      </div>
    );
  };

  return (
    <div className="trade-summary">
      <div className="trade-category">
        <div className="header">
          <p>Day</p>
        </div>
        {renderTrades(dailyTrades)}
      </div>
      <div className="trade-category">
        <div className="header">
          <p>Week</p>
        </div>{" "}
        {renderTrades(weeklyTrades)}
      </div>
      <div className="trade-category">
        <div className="header">
          <p>Month</p>
        </div>{" "}
        {renderTrades(monthlyTrades)}
      </div>
    </div>
  );
};

export default TradeSum;
