import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";
import {
  RadialBarChart,
  RadialBar,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { ProgressBar } from "react-bootstrap";
import { FaInfoCircle } from "react-icons/fa";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  const formatValue = (value, isCurrency = false) => {
    if (isNaN(value) || value === undefined) {
      return isCurrency ? "$0.00" : "N/A";
    }
    return isCurrency ? `$${value.toFixed(2)}` : `${value.toFixed(2)}`;
  };

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const {
    absoluteReturn = 0,
    winRate = 0,
    winLossRatio = 0,
    sharpeRatio = 0,
    averageWin = 0,
    averageLoss = 0,
  } = analysisResults || {};

  return (
    <div className="analysis-container">
      {/* Absolute Return Card */}
      <div className="metric-card">
        <div className="metric-header">
          <span>
            Absolute Return <FaInfoCircle />
          </span>
        </div>
        <div className="metric-content">
          <ResponsiveContainer width="100%" height={120}>
            <RadialBarChart
              innerRadius="80%"
              outerRadius="100%"
              data={[{ value: absoluteReturn }]}
            >
              <RadialBar
                dataKey="value"
                fill={absoluteReturn >= 50 ? "#34a853" : "#ea4335"}
                cornerRadius={10}
              />
              <Tooltip formatter={(value) => `${value.toFixed(2)}%`} />
            </RadialBarChart>
          </ResponsiveContainer>
          <p className="metric-value">{formatValue(absoluteReturn)}%</p>
          <p className="goal-text">Goal: 50% - 100%</p>
        </div>
      </div>

      {/* Win/Loss Rate Card */}
      <div className="metric-card">
        <div className="metric-header">
          <span>
            Win % <FaInfoCircle />
          </span>
        </div>
        <div className="metric-content">
          <ResponsiveContainer width="100%" height={120}>
            <RadialBarChart
              innerRadius="70%"
              outerRadius="100%"
              data={[{ value: winRate }]}
            >
              <RadialBar
                dataKey="value"
                fill={winRate >= 60 ? "#34a853" : "#ea4335"}
                cornerRadius={10}
              />
              <Tooltip formatter={(value) => `${value.toFixed(2)}%`} />
            </RadialBarChart>
          </ResponsiveContainer>
          <p className="metric-value">{formatValue(winRate)}%</p>
          <p className="goal-text">Goal: 60% Win Rate</p>
        </div>
      </div>

      {/* $ on Winners / $ on Losers Ratio Card */}
      <div className="metric-card">
        <div className="metric-header">
          <span>
            Win/Loss Trade Ratio <FaInfoCircle />
          </span>
        </div>
        <div className="metric-content">
          <p className="metric-value">{winLossRatio.toFixed(2)}</p>
          <ProgressBar>
            <ProgressBar
              now={(winLossRatio / 1.5) * 100}
              label={`Ratio: ${winLossRatio.toFixed(2)}`}
              variant={winLossRatio >= 1.5 ? "success" : "danger"}
            />
          </ProgressBar>
          <p className="goal-text">Goal: Minimum 1.5</p>
        </div>
      </div>

      {/* Sharpe Ratio Card */}
      <div className="metric-card">
        <div className="metric-header">
          <span>
            Sharpe Ratio <FaInfoCircle />
          </span>
        </div>
        <div className="metric-content">
          <ResponsiveContainer width="100%" height={120}>
            <RadialBarChart
              innerRadius="80%"
              outerRadius="100%"
              data={[{ value: sharpeRatio }]}
            >
              <RadialBar
                dataKey="value"
                fill={sharpeRatio >= 3 ? "#34a853" : "#ea4335"}
                cornerRadius={10}
              />
              <Tooltip formatter={(value) => value.toFixed(2)} />
            </RadialBarChart>
          </ResponsiveContainer>
          <p className="metric-value">{sharpeRatio.toFixed(2)}</p>
          <p className="goal-text">Goal: Minimum 3</p>
        </div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;

