import React from "react";
import "./styles/summary.css";

const Summary = ({ recentTrades }) => {
  // Calculate total win
  const totalWin = recentTrades.reduce((total, trade) => {
    return total + (parseFloat(trade.profit_loss) > 0 ? 1 : 0);
  }, 0);

  // Calculate total loss
  const totalLoss = recentTrades.reduce((total, trade) => {
    return total + (parseFloat(trade.profit_loss) < 0 ? 1 : 0);
  }, 0);

  // Calculate total profit/loss
  const netProfitLoss = recentTrades.reduce((total, trade) => {
    return total + parseFloat(trade.profit_loss);
  }, 0);

  // Calculate win rate
  const winRate = (totalWin / recentTrades.length) * 100;

  // Calculate average win and average loss
  const winTrades = recentTrades.filter(
    (trade) => parseFloat(trade.profit_loss) > 0
  );
  const lossTrades = recentTrades.filter(
    (trade) => parseFloat(trade.profit_loss) < 0
  );
  const avgWin =
    winTrades.reduce(
      (total, trade) => total + parseFloat(trade.profit_loss),
      0
    ) / winTrades.length;
  const avgLoss =
    lossTrades.reduce(
      (total, trade) => total + parseFloat(trade.profit_loss),
      0
    ) / lossTrades.length;

  // Calculate return on investment (ROI)
  const totalInvestment = recentTrades.reduce(
    (total, trade) =>
      total + parseFloat(trade.entry_price) * parseFloat(trade.contracts),
    0
  );
  const roi = (netProfitLoss / totalInvestment) * 100;

  // Calculate win/loss ratio
  const winLossRatio = Math.abs(avgWin / avgLoss);

  return (
    <div className="summary-container">
      <div className="summary">
        <p>Total win: {totalWin}</p>
        <p>Total loss: {totalLoss}</p>
        <p>ROI: {roi.toFixed(2)}%</p>
        <p>Win rate: {winRate.toFixed(2)}%</p>
        <p>Avg. win: ${avgWin.toFixed(2)}</p>
        <p>Avg. loss: ${avgLoss.toFixed(2)}</p>
        <p>Win/loss ratio: {winLossRatio.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default Summary;
