import React, { useState, useEffect } from "react";
import "./styles/plan.css";
import settingIcon from "./assets/setting.png";

const Plan = () => {
  const [strategies, setStrategies] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editedPlan, setEditedPlan] = useState(null); // Define editedPlan state

  useEffect(() => {
    fetchStrategies();
  }, []);

  const fetchStrategies = async () => {
    try {
      const response = await fetch("http://localhost:8000/strategies/");
      if (!response.ok) {
        throw new Error("Failed to fetch strategies");
      }
      const data = await response.json();
      setStrategies(data);
    } catch (error) {
      console.error("Error fetching strategies:", error.message);
    }
  };

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/strategies/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add strategy");
      }
      setShowModal(false);
      fetchStrategies();
    } catch (error) {
      console.error("Error adding strategy:", error);
    }
  };
  const handlePlansSettingsClick = (item, e) => {
    e.stopPropagation();
    setEditedPlan(item);
    setShowEditModal(true); // Assuming this modal is for editing plans
  };

  return (
    <div className="plan">
      <div className="header-card">
        <p>Plan</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="plan-list-container">
        <ul className="cards">
          {strategies.map((strategy) => (
            <li key={strategy.strategy_id} className="list-item">
              <div className="card-summary">
                <div className="category">
                  <h3>Plan:</h3>
                  <p>{strategy.plan}</p>
                </div>
                <div className="category">
                  <h3>Position:</h3>
                  <p>{strategy.position_sizing_rules}</p>
                </div>
                <div className="category">
                  <h3>Outcome:</h3>
                  <p>{strategy.outcome}</p>
                </div>
                <img
                  src={settingIcon}
                  alt="Setting"
                  className="setting-icon"
                  onClick={(e) => handlePlansSettingsClick(item, e)}
                />
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <Modal onClose={() => setShowModal(false)} onSubmit={handleSubmit} />
      )}
    </div>
  );
};

const Modal = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    plan: "",
    outcome: "",
    author: "",
    max_loss_tolerance: 0,
    position_sizing_rules: "",
    tags: "",
    status: "Active",
    version: 0,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <div className="card">
          <h1>New Plan</h1>
        </div>
        <form className="plan-form" onSubmit={handleSubmit}>
          <label htmlFor="enter_date">Date:</label>
          <input
            type="date"
            name="enter_date"
            value={formData.enter_date}
            onChange={handleChange}
          />
          <label htmlFor="watch_id">Watch ID:</label>
          <input
            type="text"
            name="watch_id"
            value={formData.watch_id}
            onChange={handleChange}
          />
          <label htmlFor="plan">Plan:</label>
          <input
            type="text"
            name="plan"
            value={formData.plan}
            onChange={handleChange}
          />
          <label htmlFor="outcome">Outcome:</label>
          <input
            type="text"
            name="outcome"
            value={formData.outcome}
            onChange={handleChange}
          />
          <label htmlFor="author">Author:</label>
          <input
            type="text"
            name="author"
            value={formData.author}
            onChange={handleChange}
          />
          <label htmlFor="max_loss_tolerance">Max Loss Tolerance:</label>
          <input
            type="number"
            name="max_loss_tolerance"
            value={formData.max_loss_tolerance}
            onChange={handleChange}
          />
          <label htmlFor="position_sizing_rules">Position Sizing Rules:</label>
          <input
            type="text"
            name="position_sizing_rules"
            value={formData.position_sizing_rules}
            onChange={handleChange}
          />
          <label htmlFor="tags">Tags:</label>
          <input
            type="text"
            name="tags"
            value={formData.tags}
            onChange={handleChange}
          />
          <label htmlFor="version">Version:</label>
          <input
            type="text"
            name="version"
            value={formData.version}
            onChange={handleChange}
          />
          <label htmlFor="status"></label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="modern-select"
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
            <option value="Under Review">Under Review</option>
          </select>
          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  );
};

export default Plan;
