import React, { useState, useEffect } from "react";
import "./styles/plan.css";

const Plan = () => {
  const [strategies, setStrategies] = useState([]);
  const [showModal, setShowModal] = useState(false); // State to control the visibility of the modal

  useEffect(() => {
    fetchStrategies();
  }, []);

  const fetchStrategies = async () => {
    try {
      const response = await fetch("http://localhost:8000/strategies/");
      if (!response.ok) {
        throw new Error("Failed to fetch strategies");
      }
      const data = await response.json();
      setStrategies(data);
    } catch (error) {
      console.error("Error fetching strategies:", error.message);
    }
  };

  const handleAddStrategy = async (newStrategyData) => {
    try {
      const response = await fetch("http://localhost:8000/strategies/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newStrategyData),
      });
      if (!response.ok) {
        throw new Error("Failed to add strategy");
      }
      // Fetch strategies again to update the list
      fetchStrategies();
      // Close the modal after adding the strategy
      setShowModal(false);
    } catch (error) {
      console.error("Error adding strategy:", error.message);
    }
  };

  const openModal = () => {
    setShowModal(true);
  };

  return (
    <div className="plan">
      <div className="header-card">
        <p>Plan</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={openModal} // Call openModal when the plus icon is clicked
        ></i>
      </div>
      <div className="plan-list-container">
        <ul className="cards">
          {strategies.map((strategy) => (
            <li key={strategy.strategy_id} className="list-item">
              <div className="card-summary">
                <div className="category">
                  <h3>Plan:</h3>
                  <p>{strategy.plan}</p>
                </div>
                <div className="category">
                  <h3>Position:</h3>
                  <p>{strategy.position_sizing_rules}</p>
                </div>
                <div className="category">
                  <h3>Outcome:</h3>
                  <p>{strategy.outcome}</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <Modal
          onClose={() => setShowModal(false)} // Close the modal when onClose is triggered
          onSubmit={handleAddStrategy} // Pass handleAddStrategy as a callback to submit the new strategy
        />
      )}
    </div>
  );
};

export default Plan;
