import { PieChart, Pie, Cell, ResponsiveContainer } from "recharts";
import { performAnalysis } from "./AnalysisGet";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [hoveredSegment, setHoveredSegment] = useState(null); // Track hovered segment
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const {
    averageWin = 0,
    averageLoss = 0,
    winRate = 0,
  } = analysisResults || {};
  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 50;
  const formatCash = (value) => `$${value.toFixed(2)}`;

  const data = [
    {
      name: "Average Win",
      value: winPercent,
      color: "#4a90e2",
      amount: averageWin,
    },
    {
      name: "Average Loss",
      value: 100 - winPercent,
      color: "#f44336",
      amount: averageLoss,
    },
  ];

  const winRateData = [{ name: "Win Rate", value: winRate, color: "#4a90e2" }];

  return (
    <div
      className="analysis-display-container"
      style={{ width: "150px", margin: "0 auto" }}
    >
      {/* Win Rate Chart */}
      <div style={{ marginTop: "20px", position: "relative" }}>
        <ResponsiveContainer width="100%" height={150}>
          <PieChart>
            <Pie
              data={winRateData}
              innerRadius={50}
              outerRadius={70}
              startAngle={90}
              endAngle={-270}
              stroke="none"
              dataKey="value"
            >
              {winRateData.map((entry, index) => (
                <Cell
                  key={`winrate-cell-${index}`}
                  fill={entry.color}
                  style={{ cursor: "pointer" }}
                  onMouseEnter={() =>
                    setHoveredSegment({ name: entry.name, value: entry.value })
                  }
                  onMouseLeave={() => setHoveredSegment(null)}
                />
              ))}
            </Pie>
          </PieChart>
        </ResponsiveContainer>

        {/* Centered Info for Win Rate */}
        {hoveredSegment && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              textAlign: "center",
              opacity: hoveredSegment ? 1 : 0,
              transition: "opacity 0.3s",
              pointerEvents: "none",
            }}
          >
            <p style={{ fontSize: "14px", color: "#4a90e2", margin: 0 }}>
              {hoveredSegment.name}
            </p>
            <p
              style={{
                fontSize: "18px",
                fontWeight: "bold",
                color: "#4a90e2",
                margin: 0,
              }}
            >
              {`${hoveredSegment.value.toFixed(2)}%`}
            </p>
          </div>
        )}
      </div>

      {/* Main Chart for Average Win and Loss */}
      <div style={{ marginTop: "20px", position: "relative" }}>
        <ResponsiveContainer width="100%" height={250}>
          <PieChart>
            <Pie
              data={data}
              innerRadius={50}
              outerRadius={70}
              dataKey="value"
              startAngle={90}
              endAngle={-270}
              stroke="none"
            >
              {data.map((entry, index) => (
                <Cell
                  key={`cell-${index}`}
                  fill={entry.color}
                  style={{ transition: "filter 0.3s", cursor: "pointer" }}
                  onMouseEnter={() =>
                    setHoveredSegment({ name: entry.name, value: entry.amount })
                  }
                  onMouseLeave={() => setHoveredSegment(null)}
                />
              ))}
            </Pie>
          </PieChart>
        </ResponsiveContainer>

        {/* Centered Info for Average Win and Loss */}
        {hoveredSegment && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              textAlign: "center",
              opacity: hoveredSegment ? 1 : 0,
              transition: "opacity 0.3s",
              pointerEvents: "none",
            }}
          >
            <p
              style={{
                fontSize: "14px",
                color: hoveredSegment.color || "#333",
                margin: 0,
              }}
            >
              {hoveredSegment.name}
            </p>
            <p
              style={{
                fontSize: "18px",
                fontWeight: "bold",
                color: hoveredSegment.color || "#333",
                margin: 0,
              }}
            >
              {formatCash(hoveredSegment.value)}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AnalysisDisplay;

