// Watchlist.js
import React, { useState, useEffect } from "react";
import "./Watchlist.css"; // Assuming you have a CSS file for styles

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist/")
      .then((response) => response.json())
      .then((data) => {
        setWatchlist(data.watchlist_entries);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="watchlist">
      <div className="header">
        <p className="">Watchlist</p>
        <i className="fa-solid fa-plus" id="new-btn"></i>
      </div>
      <ul>
        {watchlist.map((entry, index) => (
          <li key={index} className="watchlist-item">
            <p className="symbol">{entry[2]}</p>
            <p className="price">Price: ${entry[3]}</p>
            <p className="target-price">Target: ${entry[4]}</p>
            <p className="exp-date">Expiry: {entry[5]}</p>
            <p className="plan">{entry[6]}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Watchlist;
