import React, { useState, useEffect, useRef } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");
  const barRefs = useRef([]);

  // Fetch Data
  const fetchDailyPNLData = async () => {
    try {
      const data = await fetchDailyPnls();
      setDailyPNLData(data);
    } catch (error) {
      setError("Error fetching Daily PNL data");
      console.error("Error fetching Daily PNL data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDailyPNLData();
  }, []);

  // Data Processing Functions
  const calculateBars = () => {
    let balances = [];
    let labels = [];

    if (view === "daily") {
      const currentWeek = getCurrentWeek(dailyPNLData);
      balances = currentWeek.map((day) => parseFloat(day.balance));
      labels = currentWeek.map((day) => formatDate(day.entry_date));
    } else if (view === "weekly") {
      const currentMonth = getCurrentMonth(dailyPNLData);
      currentMonth.forEach((week) => {
        const weekSum = week.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(weekSum);
        labels.push(
          `${formatDate(week[0].entry_date)} - ${formatDate(
            week[week.length - 1].entry_date
          )}`
        );
      });
    } else if (view === "monthly") {
      const months = getMonthsData(dailyPNLData);
      months.forEach((month) => {
        const monthSum = month.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(monthSum);
        labels.push(formatMonthYear(month[0].entry_date));
      });
    }

    return { balances, labels };
  };

  const getCurrentWeek = (data) => {
    const weekData = [];
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Sun) to 6 (Sat)

    const startOfWeek = new Date(today);
    startOfWeek.setDate(today.getDate() - dayOfWeek); // Start on Sunday
    startOfWeek.setHours(0, 0, 0, 0);

    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(startOfWeek.getDate() + 6); // End on Saturday
    endOfWeek.setHours(23, 59, 59, 999);

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate >= startOfWeek && entryDate <= endOfWeek) {
        weekData.push(entry);
      }
    });

    weekData.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));
    return weekData;
  };

  const getCurrentMonth = (data) => {
    const weeks = [];
    let week = [];
    const today = new Date();
    const month = today.getMonth();

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate.getMonth() === month) {
        week.push(entry);
        // Assuming there are 5 entries per week
        if (week.length === 5) {
          weeks.push(week);
          week = [];
        }
      }
    });

    if (week.length) {
      weeks.push(week);
    }

    return weeks;
  };

  const getMonthsData = (data) => {
    const months = Array.from({ length: 12 }, () => []);
    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      months[entryDate.getMonth()].push(entry);
    });
    return months.filter((month) => month.length > 0); // Filter out any empty month arrays
  };

  const formatMonthYear = (dateStr) => {
    if (!dateStr || !dateStr.includes("-")) return "";
    const dateParts = dateStr.split("-");
    const month = dateParts[1];
    const year = dateParts[0];
    return `${month}/${year}`;
  };

  // Animate Bars using requestAnimationFrame for smoother animations
  const animateBars = (balances) => {
    balances.forEach((balance, index) => {
      const barElem = barRefs.current[index];
      if (barElem) {
        let width = 0;
        const maxBalance = Math.max(...balances);
        const percentage = (Math.abs(balance) / maxBalance) * 100;

        const animate = () => {
          if (width < percentage) {
            width++;
            barElem.style.width = width + "%";
            barElem.innerHTML = width + "%"; // Update the label if desired
            requestAnimationFrame(animate);
          }
        };
        animate(); // Start the animation
      }
    });
  };

  useEffect(() => {
    if (dailyPNLData.length === 0) return;

    const { balances, labels } = calculateBars();

    // Initialize refs only if they haven’t been initialized yet
    barRefs.current =
      balances.length > barRefs.current.length
        ? Array(balances.length)
            .fill()
            .map((_, i) => barRefs.current[i] || React.createRef())
        : barRefs.current;

    animateBars(balances);
  }, [dailyPNLData, view]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const { balances, labels } = calculateBars();

  const headerDate =
    view === "daily"
      ? new Date(dailyPNLData[0].entry_date).toLocaleDateString()
      : view === "weekly"
      ? formatMonthYear(dailyPNLData[0].entry_date)
      : dailyPNLData[0].entry_date.split("-")[0];

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            Daily
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
        <div className="header-date">{headerDate}</div>
      </div>
      <hr />
      <div className="bars-container">
        {balances.map((balance, index) => {
          const isPositive = balance >= 0;
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                ref={(el) => (barRefs.current[index] = el)}
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `0%`, // Initial width for animation
                }}
              >
                <span className="bar-value">{formatCash(balance)}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;
