// InitialCash.js

import React, { useState } from "react";
import "./styles/initialCash.css";

const InitialCash = ({ onInitialCashSubmit }) => {
  const [initialCash, setInitialCash] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ensure initial cash is a valid number
    if (!isNaN(parseFloat(initialCash))) {
      // Pass initial cash to parent component
      onInitialCashSubmit(parseFloat(initialCash));
    }
  };

  return (
    <div className="initial-cash-container">
      <h2>Enter Initial Trading Cash</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="initial-cash">Initial Cash:</label>
        <input
          id="initial-cash"
          type="number"
          placeholder="Enter initial cash amount"
          value={initialCash}
          onChange={(e) => setInitialCash(e.target.value)}
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default InitialCash;
