export const formatDate = (dateString) => {
  const date = new Date(dateString);
  const month = (date.getMonth() + 1).toString().padStart(2, "0");
  const day = date.getDate().toString().padStart(2, "0");
  const year = date.getFullYear();
  return `${month}/${day}/${year}`;
};

export const showSuccessModal = () => {
  // Create a new modal element
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "lightgreen"; // Set background color
  modal.style.position = "fixed"; // Position fixed
  modal.style.top = "20px"; // Top position
  modal.style.right = "20px"; // Right position
  modal.style.borderRadius = "2px"; // Rounded corners
  modal.style.zIndex = "9999"; // Set z-index to ensure it appears above other content
  modal.style.fontSize = "12px"; // Adjust font size as needed
  modal.style.boxShadow = "0 5px 30px 0 rgba(0, 255, 0, 0.1)";

  modal.innerHTML = `
      <div class="modal-content-success">
        <p>Success.</p>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove(); // Remove the modal from the DOM
  }, 3000);
};

export const showUnsuccessModal = (errorMessage) => {
  // Create a new modal element
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "lightred"; // Set background color
  modal.style.position = "fixed"; // Position fixed
  modal.style.top = "20px"; // Top position
  modal.style.right = "20px"; // Right position
  modal.style.borderRadius = "2px"; // Rounded corners
  modal.style.zIndex = "9999"; // Set z-index to ensure it appears above other content
  modal.style.fontSize = "12px"; // Adjust font size as needed
  modal.style.boxShadow = "0 5px 30px 0 rgba(255, 0, 0, 0.1)"; // Light red boxShadow

  modal.innerHTML = `
      <div class="modal-content-error">
        <p>${errorMessage}.</p>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove(); // Remove the modal from the DOM
  }, 3000);
};
