import React, { useState } from "react";
import "./styles/newTrade.css"; // Import the stylesheet

const NewTrade = ({ onTradeSubmit }) => {
  const [formData, setFormData] = useState({
    option_symbol: "",
    option_type: "",
    strike_price: "",
    expiration_date: "",
    entry_price: "",
    contracts: "",
    underlying_stock_price: "",
    implied_volatility: "",
    rationale: "",
    feedback: "",
    outcome: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Populate the trade date with current timestamp
    const currentDate = new Date().toISOString().split("T")[0];
    // Format expiration date to YYYY-MM-DD format
    const expirationDate = formData.expiration_date
      .split("/")
      .reverse()
      .join("-");
    // Call the callback function to submit the form data
    onTradeSubmit({
      ...formData,
      expiration_date: expirationDate,
      trade_date: currentDate,
    });
    // Clear the form after submission
    setFormData({
      option_symbol: "",
      option_type: "",
      strike_price: "",
      expiration_date: "",
      entry_price: "",
      contracts: "",
      underlying_stock_price: "",
      implied_volatility: "",
      rationale: "",
      feedback: "",
      outcome: "",
    });
  };

  return (
    <div className="grid-item-card-right">
      <div className="card">
        <p>New Trade</p>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="option_symbol"
          placeholder="Option Symbol"
          value={formData.option_symbol}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="option_type"
          placeholder="Option Type"
          value={formData.option_type}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="strike_price"
          placeholder="Strike Price"
          value={formData.strike_price}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="expiration_date"
          value={formData.expiration_date}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="entry_price"
          placeholder="Entry Price"
          value={formData.entry_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contracts"
          placeholder="Contracts"
          value={formData.contracts}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="underlying_stock_price"
          placeholder="Underlying Stock Price"
          value={formData.underlying_stock_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="implied_volatility"
          placeholder="Implied Volatility"
          value={formData.implied_volatility}
          onChange={handleChange}
          required
        />
        <textarea
          name="rationale"
          placeholder="Rationale"
          value={formData.rationale}
          onChange={handleChange}
          required
        />
        <textarea
          name="feedback"
          placeholder="Feedback"
          value={formData.feedback}
          onChange={handleChange}
          required
        />
        <textarea
          name="outcome"
          placeholder="Outcome"
          value={formData.outcome}
          onChange={handleChange}
          required
        />
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default NewTrade;
