import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
        console.log("Fetched Daily PNL Data:", data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const formatMonthYear = (dateStr) => {
    if (!dateStr || !dateStr.includes("-")) return "";
    const dateParts = dateStr.split("-");
    const month = dateParts[1].padStart(2, "0"); // Ensure two-digit month
    const year = dateParts[0];
    return `${month}/${year}`;
  };

  const calculateBars = () => {
    let balances = [];
    let labels = [];

    console.log("Calculating bars for view:", view);

    if (view === "daily") {
      const currentWeek = getCurrentWeek(dailyPNLData);
      balances = currentWeek.map((day) => parseFloat(day.balance));
      labels = currentWeek.map((day) => formatDate(day.entry_date));
    } else if (view === "weekly") {
      const weeks = getWeeksData(dailyPNLData);
      weeks.forEach((week, index) => {
        const weekSum = week.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(weekSum);
        labels.push(
          `${formatDate(week[0].entry_date)} - ${formatDate(
            week[week.length - 1].entry_date
          )}`
        );
      });
    } else if (view === "monthly") {
      const months = getMonthsData(dailyPNLData);
      months.forEach((month, index) => {
        const monthSum = month.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(monthSum);
        labels.push(formatMonthYear(month[0].entry_date));
      });
    }

    console.log("Balances:", balances);
    console.log("Labels:", labels);

    return { balances, labels };
  };

  const getCurrentWeek = (data) => {
    const weekData = [];
    const today = new Date();
    const startOfWeek = new Date(
      today.setDate(today.getDate() - today.getDay() + 1)
    ); // Adjust to Monday
    const endOfWeek = new Date(today.setDate(startOfWeek.getDate() + 6));

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate >= startOfWeek && entryDate <= endOfWeek) {
        weekData.push(entry);
      }
    });

    weekData.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));
    console.log("Filtered Week Data:", weekData);

    return weekData;
  };

  const getWeeksData = (data) => {
    const weeks = [];
    const today = new Date();
    const startOfYear = new Date(today.getFullYear(), 0, 1); // Start of the year
    const weekDays = 7;

    let week = [];
    for (
      let date = startOfYear;
      date <= today;
      date.setDate(date.getDate() + 1)
    ) {
      const currentDate = new Date(date);
      const currentWeekStart = new Date(
        currentDate.setDate(currentDate.getDate() - currentDate.getDay() + 1)
      );
      const currentWeekEnd = new Date(
        currentDate.setDate(currentWeekStart.getDate() + weekDays - 1)
      );

      week = data.filter((entry) => {
        const entryDate = new Date(entry.entry_date);
        return entryDate >= currentWeekStart && entryDate <= currentWeekEnd;
      });

      if (week.length > 0) {
        weeks.push(week);
      }
    }

    console.log("Weekly Data:", weeks);
    return weeks;
  };

  const getMonthsData = (data) => {
    const months = Array.from({ length: 12 }, () => []);
    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      months[entryDate.getMonth()].push(entry);
    });
    const nonEmptyMonths = months.filter((month) => month.length > 0);
    console.log("Monthly Data:", nonEmptyMonths);
    return nonEmptyMonths;
  };

  const { balances, labels } = calculateBars();
  const maxBalance = Math.max(...balances);
  const threshold = maxBalance || 1; // Prevent division by zero

  const headerDate =
    view === "daily"
      ? new Date(dailyPNLData[0].entry_date).toLocaleDateString()
      : view === "weekly"
      ? `${formatDate(new Date(dailyPNLData[0].entry_date))} - ${formatDate(
          new Date(dailyPNLData[dailyPNLData.length - 1].entry_date)
        )}`
      : dailyPNLData[0].entry_date.split("-")[0];

  console.log("Header Date:", headerDate);

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            Daily
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
        <div className="header-date">{headerDate}</div>
      </div>

      <hr />
      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (Math.abs(balance) / threshold) * 100;
          const isPositive = balance >= 0;
          console.log(
            `Rendering bar for ${labels[index]}: Balance = ${balance}, Percentage = ${percentage}`
          );
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `${percentage}%`,
                }}
              >
                <span className="bar-value">{formatCash(balance)}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

