import React, { useState, useEffect } from "react";
import { fetchWatchlists } from "../api/WatchlistApi";
import NewWatchlist from "./NewWatchlist";
import UpdateWatchlist from "./UpdateWatchlist"; // Import the UpdateWatchlist component
import { formatTradeDate } from "../func/functions";

const Watchlist = () => {
  const [watchlists, setWatchlists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedWatchlist, setSelectedWatchlist] = useState(null); // State to track selected watchlist

  // Fetch watchlists on component mount
  useEffect(() => {
    const getWatchlists = async () => {
      try {
        const data = await fetchWatchlists();
        setWatchlists(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getWatchlists();
  }, []);

  const handleOpenModal = (watchlist) => {
    setSelectedWatchlist(watchlist);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedWatchlist(null); // Clear selected watchlist
    setShowModal(false);
  };

  const handleSaveChanges = (updatedData) => {
    // Update the watchlist data in the state
    setWatchlists((prevWatchlists) =>
      prevWatchlists.map((wl) =>
        wl.id === selectedWatchlist.id ? { ...wl, ...updatedData } : wl
      )
    );
    handleCloseModal(); // Close the modal after saving
  };

  if (loading) {
    return <div>Loading watchlists...</div>;
  }

  if (error) {
    return <div>Error fetching watchlists: {error}</div>;
  }

  return (
    <div className="watchlist-container">
      <div className="header-card">
        <p className="title">Watchlists</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="watchlist-new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <hr />
      <div className="watch-container">
        {watchlists.map((watchlist) => (
          <div
            className="watch-item"
            key={watchlist.id}
            style={{
              borderLeft:
                watchlist.target_price > watchlist.price
                  ? "5px solid green"
                  : "5px solid red",
            }}
            onClick={() => handleOpenModal(watchlist)} // Open modal on click
          >
            <span className="watch-item-symbol">{watchlist.symbol}</span>
            <span className="watch-item-price">
              ${watchlist.price.toFixed(2)}
            </span>
            <span className="watch-item-target-price">
              ${watchlist.target_price.toFixed(2)}
            </span>
            <span className="watch-item-exp-date">
              {formatTradeDate(watchlist.exp_date)}
            </span>
            <span className="watch-item-target-hit">
              {watchlist.target_hit ? "Yes" : "No"}
            </span>
          </div>
        ))}
      </div>

      {showModal && selectedWatchlist && (
        <UpdateWatchlist
          watchlist={selectedWatchlist}
          onClose={handleCloseModal}
          onSave={handleSaveChanges}
        />
      )}
    </div>
  );
};

export default Watchlist;
