import React, { useEffect, useState } from "react";
import { FaMoneyBillWave, FaChartLine, FaCashRegister } from "react-icons/fa"; // Import icons
import { getCash } from "../api/CashApi"; // Ensure this is the correct path

const Cash = () => {
  const [cashData, setCashData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCashData = async () => {
      try {
        const data = await getCash();
        if (data && typeof data === "object" && !Array.isArray(data)) {
          setCashData(data);
        } else {
          console.warn("Expected an object but got:", data);
          setCashData(null);
        }
      } catch (error) {
        setError("Error fetching cash data");
        console.error("Error fetching cash data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCashData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  if (!cashData) return <p>No cash data available</p>;

  return (
    <div className="cash-container">
      <div className="cash-card-container">
        <div className="cash-card">
          <div className="card-content">
            <FaCashRegister className="card-icon" />
            <span className="label">Initial Cash:</span>
            <span className="value">${cashData.initial_cash}</span>
          </div>
        </div>
        <div className="cash-card">
          <div className="card-content">
            <span className="label">Available Cash:</span>
            <span className="value">${cashData.available_cash}</span>
          </div>
        </div>
        <div className="cash-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">${cashData.net_pnl}</span>
          </div>
        </div>
        <div className="cash-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />

            <span className="label">Cash Balance:</span>
            <span className="value">${cashData.cash_balance}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cash;
