import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily"); // 'daily', 'weekly', or 'monthly'

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const calculateBars = () => {
    let balances = [];

    if (view === "daily") {
      balances = dailyPNLData.map((day) => parseFloat(day.balance));
    } else if (view === "weekly") {
      let weekSum = 0;
      dailyPNLData.forEach((day, index) => {
        weekSum += parseFloat(day.balance);
        if ((index + 1) % 5 === 0 || index === dailyPNLData.length - 1) {
          balances.push(weekSum);
          weekSum = 0;
        }
      });
    } else if (view === "monthly") {
      let monthSum = 0;
      dailyPNLData.forEach((day, index) => {
        monthSum += parseFloat(day.balance);
        if ((index + 1) % 20 === 0 || index === dailyPNLData.length - 1) {
          balances.push(monthSum);
          monthSum = 0;
        }
      });
    }

    return balances;
  };

  const balances = calculateBars();
  const maxBalance = Math.max(...balances);

  return (
    <div>
      <div>
        <button onClick={() => setView("daily")}>Daily</button>
        <button onClick={() => setView("weekly")}>Weekly</button>
        <button onClick={() => setView("monthly")}>Monthly</button>
      </div>
      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (balance / maxBalance) * 100;
          return (
            <div key={index} className="bar-wrapper">
              <div className="bar" style={{ width: `${percentage}%` }}>
                {formatCash(balance)}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;
