import React, { useState } from "react";
import { formatDate } from "./Utility";
import "./styles/watchlist.css";
import closeIcon from "./assets/close.png";
import settingIcon from "./assets/setting.png";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [updatedPrice, setUpdatedPrice] = useState("");
  const [updatedTargetPrice, setUpdatedTargetPrice] = useState("");
  const [updatedExpirationDate, setUpdatedExpirationDate] = useState("");

  const handleUpdateItem = () => {
    const updatedWatchlist = watchlist.map((item) => {
      if (item.watch_id === selectedItem.watch_id) {
        return {
          ...item,
          current_price: updatedPrice,
          target_price: updatedTargetPrice,
          expiration_date: updatedExpirationDate,
        };
      }
      return item;
    });
    setWatchlist(updatedWatchlist);
    setShowModal(false);
  };

  const handleSettingsClick = (item) => {
    setSelectedItem(item);
    setUpdatedPrice(item.current_price);
    setUpdatedTargetPrice(item.target_price);
    setUpdatedExpirationDate(item.expiration_date);
    setShowModal(true);
  };

  return (
    <div className="watchlist">
      <div className="header-card">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item) => (
            <li className="list-item" key={item.watch_id}>
              <img src={closeIcon} alt="Close" className="close-icon" />
              <img
                src={settingIcon}
                alt="Setting"
                className="setting-icon"
                onClick={() => handleSettingsClick(item)}
              />
              <p>{item.symbol}</p>
              <p>${item.current_price}</p>
              <p>${item.target_price}</p>
              <p>{formatDate(item.expiration_date)}</p>
            </li>
          ))}
        </ul>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <img
              src={closeIcon}
              alt="Close"
              className="close-icon"
              onClick={() => setShowModal(false)}
            />
            <label>Current Price</label>
            <input
              type="text"
              value={updatedPrice}
              onChange={(e) => setUpdatedPrice(e.target.value)}
            />
            <label>Target Price</label>
            <input
              type="text"
              value={updatedTargetPrice}
              onChange={(e) => setUpdatedTargetPrice(e.target.value)}
            />
            <label>Expiration Date</label>
            <input
              type="text"
              value={updatedExpirationDate}
              onChange={(e) => setUpdatedExpirationDate(e.target.value)}
            />
            <button onClick={handleUpdateItem}>Update</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Watchlist;
