import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatCash } from "../func/functions";

const ProfitChart = () => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dailyPnls = await fetchDailyPnls();
        const data = prepareChartData(dailyPnls);
        setChartData(data);
      } catch (err) {
        setError("Error loading data");
        console.error("Fetch error:", err);
      }
    };

    fetchData();
  }, []);

  const prepareChartData = (pnlData) => {
    pnlData.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));

    let cumulativeBalance = 0;
    return pnlData.map((pnl) => {
      cumulativeBalance += parseFloat(pnl.balance);
      return {
        date: new Date(pnl.entry_date).toLocaleDateString(),
        balance: cumulativeBalance,
      };
    });
  };

  if (error) return <div>{error}</div>;

  return (
    <div className="profit-chart-container">
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={chartData}>
          {/* Remove or hide horizontal grid lines */}
          <XAxis dataKey="date" />
          <YAxis tickFormatter={(value) => formatCash(value)} />
          <Tooltip
            formatter={(value) => formatCash(value)}
            labelFormatter={(label) => `Date: ${label}`}
          />
          <Line
            type="monotone"
            dataKey="balance"
            stroke="#4a90e2"
            strokeWidth={2}
            dot={false}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ProfitChart;
