import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import tLogo from "../assets/T.png";
import "../styles/styles.css";
import { FaUserCircle, FaLogging } from "react-icons/fa";

const Navigation = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [userFirstName, setUserFirstName] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("access_token");
    if (token) {
      try {
        const decodedToken = JSON.parse(localStorage.getItem("user_data"));
        setUserFirstName(decodedToken ? decodedToken.fname : null);
      } catch (e) {
        console.error("Error decoding token", e);
      }
    }
  }, [localStorage.getItem("access_token")]);

  const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);

  return (
    <div className="navigation">
      <Link to="/">
        <img src={tLogo} className="logo-image" alt="T Logo" />
      </Link>
      {userFirstName ? (
        <div className="nav-container">
          <div className="search">
            <input type="text" placeholder="Search..." />
          </div>
          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/trades">Trades</Link>
            <Link to="/analysis">Analysis</Link>
            <div className="user-dropdown">
              <button className="user-btn" onClick={toggleDropdown}>
                <FaUserCircle className="user-icon" />
                <span>{userFirstName}</span>
              </button>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <Link to="/accounts">Profile</Link>
                  <Link to="/settings">Settings</Link>
                  <button
                    onClick={() => {
                      localStorage.removeItem("access_token");
                      localStorage.removeItem("user_data");
                      window.location.href = "/start";
                    }}
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="nav-links">
          <Link to="/start">Login</Link>
        </div>
      )}
    </div>
  );
};

export default Navigation;
