import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { formatDate } from "../func/functions";

const TradeSum = () => {
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [weeklyTrades, setWeeklyTrades] = useState([]);
  const [monthlyTrades, setMonthlyTrades] = useState([]);

  useEffect(() => {
    const getTrades = async () => {
      try {
        const data = await fetchTrades();
        setTrades(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getTrades();
  }, []);

  useEffect(() => {
    if (trades.length > 0) {
      filterTrades(trades);
    }
  }, [trades]);

  const filterTrades = (trades) => {
    const today = new Date();
    const startOfWeek = new Date(
      today.setDate(today.getDate() - today.getDay())
    );
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);

    const weekly = trades.filter(
      (trade) => new Date(trade.entry_date) >= startOfWeek
    );
    const monthly = trades.filter(
      (trade) => new Date(trade.entry_date) >= startOfMonth
    );

    setWeeklyTrades(weekly);
    setMonthlyTrades(monthly);
  };

  if (loading) {
    return <div>Loading trades...</div>;
  }

  if (error) {
    return <div>Error fetching trades: {error}</div>;
  }

  return (
    <div className="trade-summary-container">
      <div className="trade-summary-section">
        <h3 className="trade-summary-title">Weekly</h3>
        <div className="trade-summary-date">{formatDate(new Date())}</div>
        {weeklyTrades.map((trade) => (
          <div className="trade-summary-item" key={trade.id}>
            <span className="trade-symbol">
              {trade.symbol} ${trade.strike_price} {trade.option_type}{" "}
              {formatDate(trade.exp_date)}
            </span>
            <span className="trade-prices">{`$${trade.entry_price} - $${trade.exit_price}`}</span>
            <span className="trade-roi">{`${(trade.roi * 100).toFixed(
              2
            )}%`}</span>
          </div>
        ))}
      </div>

      <div className="trade-summary-section">
        <h3 className="trade-summary-title">Monthly</h3>
        <div className="trade-summary-date">
          {formatDate(new Date(), "MM/yyyy")}
        </div>
        {monthlyTrades.map((trade) => (
          <div className="trade-summary-item" key={trade.id}>
            <span className="trade-symbol">
              {trade.symbol} ${trade.strike_price} {trade.option_type}{" "}
              {formatDate(trade.exp_date)}
            </span>
            <span className="trade-prices">{`$${trade.entry_price} - $${trade.exit_price}`}</span>
            <span className="trade-roi">{`${(trade.roi * 100).toFixed(
              2
            )}%`}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TradeSum;
