import React, { useState, useEffect } from "react";
import { successModal, unsuccessModal } from "./Utility";
import TradeForm from "./TradeForm";
import Modal from "./Modal";

const Trade = () => {
  const [trades, setTrades] = useState([]);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const fetchTrades = async () => {
    try {
      const response = await fetch("http://localhost:8000/trades");
      if (!response.ok) {
        throw new Error("Failed to fetch trades data");
      }
      const data = await response.json();
      setTrades(data.trades);
      setSuccessMessage("Trades data fetched successfully.");
    } catch (error) {
      setError(error.message);
      setErrorMessage("Failed to fetch trades data.");
    }
  };

  useEffect(() => {
    fetchTrades();
  }, []);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="trade">
      <div className="header">
        <p className="">Trades</p>
        <i className="fa-solid fa-plus" id="new-btn" onClick={openModal}></i>
      </div>
      <table>{/* Table rendering */}</table>
      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <TradeForm />
      </Modal>
      {successMessage && (
        <Modal isOpen={true} onClose={() => setSuccessMessage("")}>
          <div>
            <i className="fa-solid fa-check"></i>
            <p>{successMessage}</p>
          </div>
        </Modal>
      )}
      {errorMessage && (
        <Modal isOpen={true} onClose={() => setErrorMessage("")}>
          <div>
            <i className="fa-solid fa-times"></i>
            <p>{errorMessage}</p>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default Trade;
