import React, { useState, useEffect } from "react";
import { fetchWatch } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    date: "",
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  const fetchWatchlistData = async () => {
    try {
      const watchlistData = await fetchWatch();
      setWatchlist(watchlistData);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWatchlistData();
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission for adding watchlist item
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/watchlist/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add Watchlist item");
      }
      successModal();
      setShowModal(false);
      setFormData({
        date: "",
        symbol: "",
        price: "",
        target_price: "",
        exp_date: "",
        plan: "",
      });
      await fetchWatchlistData(); // Re-fetch the watchlist after adding a new item
    } catch (error) {
      console.error("Error adding Watchlist item:", error);
      unsuccessModal(error.message);
    }
  };

  // Function to handle clicking on a watchlist item for editing
  const handleListItemClick = (watchlistItem) => {
    setSelectedWatch(watchlistItem);
    setShowEditModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="watchlist">
      <div className="header">
        <p>Watchlist</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {watchlist.map((entry) => (
            <li
              key={entry.id}
              className="card-content"
              onClick={() => handleListItemClick(entry)}
            >
              <div className="left">
                <p className="symbol">{entry.symbol}</p>
              </div>
              <div className="right">
                <p className="date">
                  Date: {entry.date?.split("-").slice(1).join("/")}
                </p>
                <p className="price">Price: ${entry.price}</p>
                <p className="target-price">Target: ${entry.target_price}</p>
                <p className="exp-date">
                  Expiry: {entry.exp_date?.split("-").slice(1).join("/")}
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <p>Watchlist</p>
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
            </div>
            <form className="new-trade-form" onSubmit={handleSubmit}>
              <label htmlFor="date">Date:</label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
              />
              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
              />

              <label htmlFor="price">Price:</label>
              <input
                type="text"
                name="price"
                value={formData.price}
                onChange={handleChange}
              />

              <label htmlFor="target_price">Target Price:</label>
              <input
                type="text"
                name="target_price"
                value={formData.target_price}
                onChange={handleChange}
              />

              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={formData.exp_date}
                onChange={handleChange}
              />

              <label htmlFor="plan">Plan:</label>
              <input
                name="plan"
                value={formData.plan}
                onChange={handleChange}
              ></input>

              <button type="submit">Add</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && selectedWatch && (
        <div className="form-modal">{/* Edit form modal content here */}</div>
      )}
    </div>
  );
};

export default Watchlist;
