// components/Summary.js
import React, { useEffect, useState } from "react";
import { fetchSummary } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility";
const Summary = () => {
  const [summary, setSummary] = useState([]);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedSummary, setSelectedSummary] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    category: "",
    summary: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedSummary({ ...selectedSummary, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchSummaryData = async () => {
    try {
      const summaryData = await fetchSummary();
      if (Array.isArray(summaryData)) {
        setSummary(summaryData);
        successModal();
      } else {
        throw new Error("Fetched data is not an array");
      }
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };
  useEffect(() => {
    fetchSummaryData();
  }, []);

  const handleItemClick = (summary) => {
    setSelectedSummary(summary);
    setShowEditModal(true);
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const [year, month, day] = dateStr.split("-");
    return `${year}-${month}-${day}`;
  };

  handle;
  return (
    <div className="summary">
      <h2>Summary</h2>
      {/* Add summary implementation here */}
      <p>Summary of trades and outcome.</p>
    </div>
  );
};

export default Summary;
