// Utility.js

// Success / Unsuccess Modals

export const successModal = () => {
  const modal = document.createElement("div");
  modal.classList.add("modal-content", "modal-content-success");
  document.body.appendChild(modal);
  modal.innerHTML = `
        <div>
          <i class="fa-solid fa-check"></i>
        </div>
      `;
  setTimeout(() => {
    modal.remove();
  }, 3000);
};

export const unsuccessModal = (errorMessage) => {
  const modal = document.createElement("div");
  modal.classList.add("modal-content", "modal-content-error");
  modal.innerHTML = `
        <div>
          <p>${errorMessage}.</p>
        </div>
      `;
  document.body.appendChild(modal);
  setTimeout(() => {
    modal.remove();
  }, 3000);
};
