import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const { averageWin = 0, averageLoss = 0 } = analysisResults || {};

  // Calculate win and loss percentages for the bar
  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 50;
  const lossPercent = total > 0 ? (averageLoss / total) * 100 : 50;

  return (
    <div className="analysis-container">
      <div className="progress">
        <div
          className="progress-bar win-bar"
          style={{ width: `${winPercent}%`, marginRight: "50%" }}
        ></div>
        <div className="progress-split"></div>
        <div
          className="progress-bar loss-bar"
          style={{ width: `${lossPercent}%`, marginLeft: "50%" }}
        ></div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;

