import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  const [accountBalance, setAccountBalance] = useState(86);

  useEffect(() => {
    calculateAccountBalance();
  }, [trades]);

  const calculateAccountBalance = () => {
    let totalProfitLoss = 0;
    trades.forEach((trade) => {
      totalProfitLoss += parseFloat(trade.profit_loss);
    });
    const updatedBalance = accountBalance + totalProfitLoss;
    setAccountBalance(updatedBalance);
  };

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">{accountBalance.toFixed(2) * 100}</p>
      </div>
      <AccountGraph />
    </div>
  );
};

export default Account;

