// Trades.js

import React, { useState, useEffect } from "react";
import { fetchTrades } from "./ApiCalls"; // Import the fetchTrades function
import { successModal, unsuccessModal } from "./Utility";

const Trade = () => {
  const [trades, setTrades] = useState([]); // eslint-disable-next-line
  const [error, setError] = useState(null); // eslint-disable-next-line
  const [showModal, setShowModal] = useState(false); // eslint-disable-next-line
  const [selectedTrade, setSelectedTrade] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    symbol: "",
    option_type: "",
    strike_price: "",
    exp_date: "",
    entry_price: "",
    exit_price: "",
    contracts: "",
    entry_date: "",
    close_date: "",
    // profit_loss: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedTrade({ ...selectedTrade, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchTradesData = async () => {
    try {
      const tradesData = await fetchTrades();
      setTrades(tradesData);
      successModal();
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };

  useEffect(() => {
    fetchTradesData();
  }, []);

  const handleRowClick = (trade) => {
    setSelectedTrade(trade);
    setShowEditModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const exitPrice =
        formData.exit_price === "" || formData.exit_price === null
          ? 0
          : formData.exit_price;

      const response = await fetch("http://localhost:8000/trades", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ ...formData, exit_price: exitPrice }), // Send exit_price as 0 if empty
      });
      console.log(formData);
      if (!response.ok) {
        throw new Error("Failed to add Trade");
      }
      successModal();
      setShowModal(false);
      setFormData({
        symbol: "",
        option_type: "",
        strike_price: "",
        exp_date: "",
        entry_price: "",
        exit_price: "",
        contracts: "",
        entry_date: "",
        close_date: "",
      });
      fetchTradesData();
    } catch (error) {
      console.error("Error adding Trade:", error);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    // Ensure dates are in the correct format
    const formattedTrade = {
      ...selectedTrade,
      expDate: new Date(selectedTrade.expDate).toISOString().split("T")[0],
      entryDate: new Date(selectedTrade.entryDate).toISOString().split("T")[0],
      closeDate: new Date(selectedTrade.closeDate).toISOString().split("T")[0],
    };

    try {
      const response = await fetch(
        `http://localhost:8000/trades/${selectedTrade.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formattedTrade),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update Trade");
      }
      successModal();
      setShowEditModal(false);
      fetchTradesData();
    } catch (error) {
      console.error("Error updating Trade:", error);
    }
  };

  return (
    <div className="trade">
      <div className="header">
        <p className="">Trades</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Options</th>
            <th>Trade Date</th>
            <th>Settle Date</th>
            <th>Price</th>
            <th>Principle</th>
            <th>QTY</th>
            <th>Exit</th>
            <th>Net Amount</th>
            <th>P/L</th>
            <th>%</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((trade) => (
            <tr key={trade.id} onClick={() => handleRowClick(trade)}>
              <td>{trade.id} </td>
              <td>
                {trade.symbol} {trade.optionType} ${trade.strikePrice}{" "}
                {trade.expDate.split("-").slice(1).join("/")}
              </td>
              <td>{trade.entryDate.split("-").slice(1).join("/")}</td>
              <td>{trade.closeDate.split("-").slice(1).join("/")}</td>
              <td>${trade.entryPrice.toFixed(2)}</td>
              <td>${(trade.entryPrice * trade.contracts * 100).toFixed(2)}</td>
              <td>{trade.contracts}</td>
              <td>${trade.exitPrice}</td>
              <td>
                {trade.exitPrice && trade.contracts
                  ? `$${(trade.exitPrice * trade.contracts * 100).toFixed(2)}`
                  : ""}
              </td>
              <td>
                {trade.profitLoss && trade.contracts
                  ? `$${(trade.profitLoss * trade.contracts * 100).toFixed(2)}`
                  : ""}
              </td>
              <td>
                {trade.profitLoss
                  ? `${(
                      ((trade.exitPrice - trade.entryPrice) /
                        trade.entryPrice) *
                      100
                    ).toFixed(2)}%`
                  : ""}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
              <div>Trade</div>
            </div>

            <form className="new-trade-form" onSubmit={handleSubmit}>
              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
              />

              <label htmlFor="option_type">Option Type:</label>
              <input
                type="text"
                name="option_type"
                value={formData.option_type}
                onChange={handleChange}
              />

              <label htmlFor="strike_price">Strike Price:</label>
              <input
                type="number"
                name="strike_price"
                value={formData.strike_price}
                onChange={handleChange}
              />

              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={formData.exp_date}
                onChange={handleChange}
              />

              <label htmlFor="entry_price">Entry Price:</label>
              <input
                type="number"
                name="entry_price"
                value={formData.entry_price}
                onChange={handleChange}
              />

              <label htmlFor="exit_price">Exit Price:</label>
              <input
                type="number"
                name="exit_price"
                value={formData.exit_price}
                onChange={handleChange}
              />

              <label htmlFor="contracts">Contracts:</label>
              <input
                type="number"
                name="contracts"
                value={formData.contracts}
                onChange={handleChange}
              />

              <label htmlFor="entry_date">Entry Date:</label>
              <input
                type="date"
                name="entry_date"
                value={formData.entry_date}
                onChange={handleChange}
              />

              <label htmlFor="close_date">Close Date:</label>
              <input
                type="date"
                name="close_date"
                value={formData.close_date}
                onChange={handleChange}
              />

              <button type="submit">Add Trade</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && selectedTrade && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <span className="close" onClick={() => setShowEditModal(false)}>
                &times;
              </span>
              <div>{selectedTrade.symbol}</div>
            </div>

            <form className="edit-trade-form" onSubmit={handleEditSubmit}>
              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={selectedTrade.symbol}
                onChange={handleChange}
              />
              <label htmlFor="option_type">Option Type:</label>
              <input
                type="text"
                name="option_type"
                value={selectedTrade.optionType}
                onChange={handleChange}
              />
              <label htmlFor="strike_price">Strike Price:</label>
              <input
                type="number"
                name="strike_price"
                value={selectedTrade.strikePrice}
                onChange={handleChange}
              />
              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={selectedTrade.expDate}
                onChange={handleChange}
              />
              <label htmlFor="entry_price">Entry Price:</label>
              <input
                type="number"
                name="entry_price"
                value={selectedTrade.entryPrice}
                onChange={handleChange}
              />
              <label htmlFor="exit_price">Exit Price:</label>
              <input
                type="number"
                name="exit_price"
                value={selectedTrade.exitPrice}
                onChange={handleChange}
              />
              <label htmlFor="contracts">Contracts:</label>
              <input
                type="number"
                name="contracts"
                value={selectedTrade.contracts}
                onChange={handleChange}
              />
              <label htmlFor="entry_date">Entry Date:</label>
              <input
                type="date"
                name="entry_date"
                value={selectedTrade.entryDate}
                onChange={handleChange}
              />
              <label htmlFor="close_date">Close Date:</label>
              <input
                type="date"
                name="close_date"
                value={selectedTrade.closeDate}
                onChange={handleChange}
              />{" "}
              <label htmlFor="profit_loss">P/L</label>
              <input
                type="text"
                name="profit_loss"
                value={selectedTrade.profitLoss}
                onChange={handleChange}
              />
              <button type="submit">Update</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

