import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";
import { Line } from "rc-progress";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  const formatValue = (value, isCurrency = false) => {
    if (isNaN(value) || value === undefined) {
      return isCurrency ? "$0.00" : "N/A";
    }
    return isCurrency ? `$${value.toFixed(2)}` : `${value.toFixed(2)}`;
  };

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const {
    absoluteReturn = 0,
    sharpeRatio = 0,
    winRate = 0,
    numberOfTrades = 0,
    winningTrades = 0,
    losingTrades = 0,
    averageWin = 0,
    averageLoss = 0,
    riskRewardRatio = 0,
  } = analysisResults || {};

  const absoluteReturnPercentage = (absoluteReturn / 80) * 100;
  const sharpeRatioPercentage = (sharpeRatio / 3) * 100;

  return (
    <div className="analysis-container">
      <div className="analysis-metrics-row">
        {/* Absolute Return Metric */}
        <div className="analysis-metric-top">
          <h3>Absolute Return</h3>
          <p>{`${absoluteReturn.toFixed(2)}%`}</p>
          <Line
            percent={absoluteReturnPercentage}
            strokeWidth="2"
            trailWidth="2"
            strokeColor={"#4a90e2"}
            trailColor="#d8e3ef"
          />
        </div>

        {/* Sharpe Ratio Metric */}
        <div className="analysis-metric-top">
          <h3>Sharpe Ratio</h3>
          <p>{formatValue(sharpeRatio)}</p>
          <Line
            percent={sharpeRatioPercentage}
            strokeWidth="2"
            trailWidth="2"
            strokeColor={"#4a90e2"}
            trailColor="#d8e3ef"
          />
        </div>
      </div>

      {/* Trades, Wins, and Losses Row */}
      <div className="analysis-row">
        <div className="analysis-circle">
          <h3>Trades</h3>
          <p>{numberOfTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Wins</h3>
          <p>{winningTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Losses</h3>
          <p>{losingTrades}</p>
        </div>
      </div>

      {/* Additional Metrics Row */}
      <div className="analysis-metrics-row">
        <div className="analysis-metric">
          <h3>Win Rate</h3>
          <p>{formatValue(winRate)}%</p>
        </div>
        <div className="analysis-metric">
          <h3>Avg. Win</h3>
          <p>{formatValue(averageWin, true)}</p>
        </div>
        <div className="analysis-metric">
          <h3>Avg. Loss</h3>
          <p>{formatValue(averageLoss, true)}</p>
        </div>
        <div className="analysis-metric">
          <h3>Risk Reward</h3>
          <p>{formatValue(riskRewardRatio)}</p>
        </div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;
