import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  // Initial balance
  const initialBalance = 86.0;

  const [totalAmount, setTotalAmount] = useState(initialBalance);

  useEffect(() => {
    // Calculate total profit based on 'profit' field in each trade
    const totalProfit = trades.reduce((total, trade) => {
      if (trade.exit_price) {
        if (trade.profit > 0) {
          return total + trade.profit / 100; // assuming profit is in cents
        } else {
          return total - Math.abs(trade.profit) / 100; // assuming profit is in cents
        }
      }

      return total;
    }, 0);

    // Calculate total amount in the account
    const newTotalAmount = initialBalance + totalProfit;

    // Update total amount only if the calculation results in a valid number
    if (!isNaN(newTotalAmount)) {
      setTotalAmount(newTotalAmount);
    }
  }, [trades]);

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">${totalAmount.toFixed(2)}</p>{" "}
        {/* Display with 2 decimal places */}
      </div>
      <AccountGraph />
    </div>
  );
};

export default Account;

