import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");
  const [animatedWidths, setAnimatedWidths] = useState([]);
  const [balances, setBalances] = useState([]);
  const [labels, setLabels] = useState([]);
  const threshold = 200; // Default threshold for calculation, can be adjusted or calculated dynamically.

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDailyPNLData();
  }, []);

  useEffect(() => {
    if (dailyPNLData.length > 0) {
      const { balances: newBalances, labels: newLabels } = calculateBars();
      setBalances(newBalances);
      setLabels(newLabels);
    }
  }, [dailyPNLData, view]); // Update balances and labels whenever data or view changes

  useEffect(() => {
    if (balances.length > 0) {
      const maxBalance = Math.max(...balances);
      const newThreshold = maxBalance * 2 || 1; // Fallback for zero maxBalance
      const newWidths = balances.map(
        (balance) => (Math.abs(balance) / newThreshold) * 100
      );
      setAnimatedWidths(newWidths);
    }
  }, [balances]); // Update animated widths when balances change

  const calculateBars = () => {
    let balances = [];
    let labels = [];

    if (view === "daily") {
      const currentWeek = getCurrentWeek(dailyPNLData);
      balances = currentWeek.map((day) => parseFloat(day.balance));
      labels = currentWeek.map((day) => formatDate(day.entry_date));
    } else if (view === "weekly") {
      const currentMonth = getCurrentMonth(dailyPNLData);
      currentMonth.forEach((week) => {
        const weekSum = week.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(weekSum);
        labels.push(
          `${formatDate(week[0].entry_date)} - ${formatDate(
            week[week.length - 1].entry_date
          )}`
        );
      });
    } else if (view === "monthly") {
      const months = getMonthsData(dailyPNLData);
      months.forEach((month) => {
        const monthSum = month.reduce(
          (sum, day) => sum + parseFloat(day.balance),
          0
        );
        balances.push(monthSum);
        labels.push(formatMonthYear(month[0].entry_date));
      });
    }

    return { balances, labels };
  };

  const formatMonthYear = (dateStr) => {
    if (!dateStr || !dateStr.includes("-")) return "";
    const dateParts = dateStr.split("-");
    const month = dateParts[1];
    const year = dateParts[0];
    return `${month}/${year}`;
  };

  const getCurrentWeek = (data) => {
    const weekData = [];
    const today = new Date();
    const startOfWeek = new Date(
      today.setDate(today.getDate() - today.getDay())
    );
    const endOfWeek = new Date(today.setDate(today.getDate() + 6));

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate >= startOfWeek && entryDate <= endOfWeek) {
        weekData.push(entry);
      }
    });

    weekData.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));

    return weekData;
  };

  const getCurrentMonth = (data) => {
    const weeks = [];
    let week = [];
    const today = new Date();
    const month = today.getMonth();

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate.getMonth() === month) {
        week.push(entry);
      }
    });

    while (week.length) {
      weeks.push(week.splice(0, 5)); // Assume a week has at most 5 entries
    }

    return weeks;
  };

  const getMonthsData = (data) => {
    const months = Array.from({ length: 12 }, () => []);
    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      months[entryDate.getMonth()].push(entry);
    });

    return months.filter((month) => month.length > 0);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const headerDate =
    view === "daily"
      ? new Date(dailyPNLData[0].entry_date).toLocaleDateString()
      : view === "weekly"
      ? formatMonthYear(dailyPNLData[0].entry_date)
      : dailyPNLData[0].entry_date.split("-")[0];

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            Daily
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
        <div className="header-date">{headerDate}</div>
      </div>

      <hr />
      <div className="bars-container">
        {animatedWidths.map((width, index) => {
          const isPositive = balances[index] >= 0;
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `${width}%`,
                }}
              >
                <span className="bar-value">{formatCash(balances[index])}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;

