import React, { useState, useEffect } from "react";
import "./styles/summary.css";

const Summary = ({ recentTrades }) => {
  // State to manage form submission
  const [submitted, setSubmitted] = useState(false);
  // State to manage initial cash input
  const [initialCash, setInitialCash] = useState("");
  // State to manage current cash
  const [currentCash, setCurrentCash] = useState(0);

  // Load initial cash from local storage on component mount
  useEffect(() => {
    const storedInitialCash = localStorage.getItem("initialCash");
    if (storedInitialCash) {
      setInitialCash(parseFloat(storedInitialCash));
    }
  }, []);

  // Calculate net profit/loss
  const netProfitLoss = recentTrades.reduce(
    (total, trade) => total + parseFloat(trade.profit_loss || 0),
    0
  );

  // Update current cash whenever initialCash or netProfitLoss changes
  useEffect(() => {
    if (initialCash !== "") {
      setCurrentCash(parseFloat(initialCash) + netProfitLoss);
    }
  }, [initialCash, netProfitLoss]);

  // Check if recentTrades is empty or not provided
  if (!recentTrades || recentTrades.length === 0) {
    return <div style="font-size: 10px">Fetching Trade Summary...</div>;
  }

  // Calculate other metrics (winRate, avgWin, avgLoss, roi, winLossRatio)
  const totalWin = recentTrades.reduce(
    (total, trade) => total + (parseFloat(trade.profit_loss) > 0 ? 1 : 0),
    0
  );
  const totalLoss = recentTrades.reduce(
    (total, trade) => total + (parseFloat(trade.profit_loss) < 0 ? 1 : 0),
    0
  );
  const winRate =
    recentTrades.length > 0 ? (totalWin / recentTrades.length) * 100 : 0;
  const winTrades = recentTrades.filter(
    (trade) => parseFloat(trade.profit_loss) > 0
  );
  const lossTrades = recentTrades.filter(
    (trade) => parseFloat(trade.profit_loss) < 0
  );
  const avgWin =
    winTrades.length > 0
      ? winTrades.reduce(
          (total, trade) => total + parseFloat(trade.profit_loss || 0),
          0
        ) / winTrades.length
      : 0;
  const avgLoss =
    lossTrades.length > 0
      ? lossTrades.reduce(
          (total, trade) => total + parseFloat(trade.profit_loss || 0),
          0
        ) / lossTrades.length
      : 0;
  const totalInvestment = recentTrades.reduce(
    (total, trade) =>
      total +
      parseFloat(trade.entry_price || 0) * parseFloat(trade.contracts || 0),
    0
  );
  const roi =
    totalInvestment !== 0 ? (netProfitLoss / totalInvestment) * 100 : 0;
  const winLossRatio = avgLoss !== 0 ? Math.abs(avgWin / avgLoss) : 0;

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    setCurrentCash(parseFloat(initialCash) + netProfitLoss);
  };

  const handleEdit = () => {
    setSubmitted(false);
  };

  return (
    <div className="summary-container">
      <div className="summary">
        <div className="header-card">
          <p>Summary</p>
        </div>
        <div className="details-cards">
          <div className="summary-item">
            <form
              className={`initial ${submitted ? "submitted" : ""}`}
              onSubmit={handleSubmit}
            >
              <input
                id="initial-cash"
                type="number"
                placeholder="Initial Cash"
                value={initialCash}
                onChange={(e) => setInitialCash(e.target.value)}
                required
                // disabled={submitted}
              />
              <button
                // type="button"
                onClick={handleEdit}
                className={submitted ? "submitted" : ""}
              >
                {submitted ? "Update" : "Update"}
              </button>
            </form>
          </div>
          <div className="summary-item">
            <p className="title">Cash:</p>
            <p className="detail">${currentCash.toFixed(2)}</p>
          </div>
          <div className="summary-item">
            <p className="title">Net P&L:</p>
            <p className="detail">${netProfitLoss.toFixed(2) * 100}</p>
          </div>
          <div className="summary-item">
            <p className="title">RoI:</p>
            <p className="detail">{roi.toFixed(2)}%</p>
          </div>
          <div className="summary-item">
            <p className="title">Profits:</p>
            <p className="detail">${avgWin.toFixed(2) * 100}</p>
          </div>
          <div className="summary-item">
            <p className="title">Loss:</p>
            <p className="detail">${avgLoss.toFixed(2) * 100}</p>
          </div>
          <div className="summary-item">
            <p className="title">Total Win:</p>
            <p className="detail">{totalWin}</p>
          </div>
          <div className="summary-item">
            <p className="title">Total Loss:</p>
            <p className="detail">{totalLoss}</p>
          </div>
          <div className="summary-item">
            <p className="title">Win Rate:</p>
            <p className="detail">{winRate.toFixed(2)}%</p>
          </div>
          <div className="summary-item">
            <p className="title">W/L Ratio:</p>
            <p className="detail">{winLossRatio.toFixed(2)}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Summary;
