import React, { useState, useEffect } from "react";
import { fetchMiscEntries, updateMisc } from "../api/MiscAPI";
import NewMisc from "./NewMisc";
import UpdateMisc from "./UpdateMisc";
import { formatDate } from "../func/functions";

const Misc = () => {
  const [miscEntries, setMiscEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedMisc, setSelectedMisc] = useState(null);
  const [isEditing, setIsEditing] = useState(false); // State to differentiate between adding and editing

  useEffect(() => {
    const getMiscEntries = async () => {
      try {
        const data = await fetchMiscEntries();
        setMiscEntries(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getMiscEntries();
  }, []);

  const handleOpenModal = (misc = null) => {
    setSelectedMisc(misc);
    setIsEditing(!!misc); // Determine if we're editing based on whether a misc entry is passed
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedMisc(null);
    setIsEditing(false); // Reset to not editing
    setShowModal(false);
  };

  const handleSaveChanges = async (updatedData) => {
    if (isEditing) {
      try {
        await updateMisc(selectedMisc.id, updatedData); // Update the backend
        setMiscEntries((prevEntries) =>
          prevEntries.map((item) =>
            item.id === selectedMisc.id ? { ...item, ...updatedData } : item
          )
        );
      } catch (error) {
        console.error("Error updating misc entry:", error);
      }
    } else {
      // Handle the logic for adding a new misc entry if necessary
      // This can be similar to how we handle updates, but with a different API call
    }
    handleCloseModal();
  };

  if (loading) {
    return <div>Loading entries...</div>;
  }

  if (error) {
    return <div>Error fetching entries: {error}</div>;
  }

  return (
    <div className="misc-container">
      <div className="header-card">
        <p className="title">Miscellaneous Entries</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="misc-new-btn"
          onClick={() => handleOpenModal()} // Open modal without an entry to add a new one
        ></i>
      </div>
      <hr />
      <div className="misc-container">
        {miscEntries.map((entry) => (
          <div
            className="misc-item"
            key={entry.id}
            style={{
              borderLeft:
                new Date(entry.entry_date) > new Date() // Example condition for styling
                  ? "5px solid green"
                  : "5px solid red",
            }}
            onClick={() => handleOpenModal(entry)}
          >
            <span className="misc-item-category">{entry.category}</span>
            <span className="misc-item-entry-date">
              {formatDate(entry.entry_date)}
            </span>
            <span className="misc-item-description">{entry.description}</span>
          </div>
        ))}
      </div>

      {showModal && isEditing && selectedMisc && (
        <UpdateMisc
          misc={selectedMisc}
          onClose={handleCloseModal}
          onSave={handleSaveChanges}
        />
      )}

      {showModal && !isEditing && (
        <NewMisc onClose={handleCloseModal} onSave={handleSaveChanges} />
      )}
    </div>
  );
};

export default Misc;
