import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  // Initial balance
  const initialBalance = 86.0;

  const [totalAmount, setTotalAmount] = useState(initialBalance);

  useEffect(() => {
    // Calculate total profits
    const totalProfits =
      trades.reduce((total, trade) => total + (trade.profit_loss || 0), 0) /
      100; // Use 0 if profit_loss is null

    // Calculate total amount in the account
    const newTotalAmount = initialBalance + totalProfits;

    // Update total amount only if the calculation results in a valid number
    if (!isNaN(newTotalAmount)) {
      setTotalAmount(newTotalAmount);
    }
  }, [trades]);

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">${totalAmount.toFixed(2)}</p>{" "}
        {/* Display with 2 decimal places */}
      </div>
      <AccountGraph />
    </div>
  );
};

export default Account;
