import React, { useState, useEffect } from "react";
import { fetchTransactions } from "../api/TransactionsAPI";
import { formatTradeDate } from "../func/functions"; // Adjust according to your date formatting function
import NewTransaction from "./NewTransaction";

const TransactionTable = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch transactions on component mount
  useEffect(() => {
    const getTransactions = async () => {
      try {
        const data = await fetchTransactions();
        setTransactions(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getTransactions();
  }, []);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error fetching transactions: {error}</div>;
  }

  return (
    <div className="transaction-table-container">
      <div className="header-card">
        <p className="title">Transactions</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="transaction-new-btn"
          onClick={handleOpenModal}
        ></i>
      </div>
      <hr />
      <table className="transaction-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Summary</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{formatTradeDate(transaction.transaction_date)}</td>
              <td>{transaction.transaction_type}</td>
              <td>${transaction.amount}</td>
              <td>{transaction.transaction_summary}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && <NewTransaction onClose={handleCloseModal} />}
    </div>
  );
};

export default TransactionTable;
