import React, { useState, useRef } from "react";
import "./styles/newTrade.css";

const NewWatch = ({ onAdd }) => {
  const [formData, setFormData] = useState({
    enter_date: "",
    symbol: "",
    current_price: "",
    target_price: "",
    expiration_date: "",
  });

  const symbolInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/watchlist", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add watchlist item");
      }
      // Reset the form data state to empty values
      setFormData({
        enter_date: "",
        symbol: "",
        current_price: "",
        target_price: "",
        expiration_date: "",
      });
      const data = await response.json();
      onAdd([...watchlist, newItem]); // Update the list state with the newly added watchlist item
    } catch (error) {
      console.error("Error adding watchlist item:", error);
    }
  };
  const handlePlusIconClick = () => {
    symbolInputRef.current.focus();
  };

  return (
    <div className="new-trade">
      <div className="card">
        <p>New Watchlist Item</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={handlePlusIconClick}
        ></i>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <ul>
          <li>
            <label htmlFor="enter_date">Date:</label>
            <input
              type="date"
              name="enter_date"
              ref={symbolInputRef}
              value={formData.enter_date}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="symbol">Symbol:</label>
            <input
              type="text"
              name="symbol"
              ref={symbolInputRef}
              value={formData.symbol}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="current_price">Current Price:</label>
            <input
              type="text"
              name="current_price"
              value={formData.current_price}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="target_price">Target Price:</label>
            <input
              type="text"
              name="target_price"
              value={formData.target_price}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="expiration_date">Expiration Date:</label>
            <input
              type="date"
              name="expiration_date"
              value={formData.expiration_date}
              onChange={handleChange}
              required
            />
          </li>
        </ul>
        <input type="submit" value="Add Item" />
      </form>
    </div>
  );
};

export default NewWatch;
