      const monthKey = `${date.getFullYear()}-${date.getMonth() + 1}`;

      // Check if this is the first entry for the month
      if (!monthlyData[monthKey]) {
        monthlyData[monthKey] = {
          initial_balance: entry.balance, // First entry's balance is the initial balance
          balance: entry.balance,
          roi: 0, // Initial ROI starts at 0
          open_cash: entry.open_cash,
          close_cash: entry.close_cash,
        };
      } else {
        // Update balance and cash values
        monthlyData[monthKey].balance += entry.balance;
        monthlyData[monthKey].open_cash += entry.open_cash;
        monthlyData[monthKey].close_cash += entry.close_cash;

        // Update ROI: Calculate it based on the initial balance for the month
        const currentBalance = monthlyData[monthKey].balance;
        const initialBalance = monthlyData[monthKey].initial_balance;
        monthlyData[monthKey].roi =
          ((currentBalance - initialBalance) / initialBalance) * 100;
      }

      // If you need to carry the balance forward:
      if (index < dailyPNLData.length - 1) {
        dailyPNLData[index + 1].balance += entry.balance;
      }
    });

    console.log(monthlyData);
    return monthlyData;
  };

