// Cash.js

import React, { useState, useEffect } from "react";
import "./styles/styles.css";

const Cash = () => {
  const [cash, setCash] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  // State to indicate if data is loading
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch cash
  const fetchCash = async () => {
    try {
      const response = await fetch("http://localhost:8000/cash");
      if (!response.ok) {
        throw new Error("Failed to fetch cash data");
      }
      const data = await response.json();
      setCash(data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // Fetch analysis
  const fetchAnalysis = async () => {
    try {
      const response = await fetch("http://localhost:8000/analysis");
      if (!response.ok) {
        throw new Error("Failed to fetch analysis data");
      }
      const data = await response.json();
      setAnalysis(data);
    } catch (error) {
      setError(error.message);
    }
  };

  // UseEffect hook to fetch cash data when component mounts
  useEffect(() => {
    fetchCash();
    fetchAnalysis();
  }, []);

  if (loading || !analysis) {
    return <p>Loading cash information...</p>;
  }
  if (error) {
    return <p>Error fetching cash data: {error}</p>;
  }

  // Extract current_cash from analysis data
  const currentCash = analysis.current_cash;

  return (
    <div className="cash">
      <div className="header">Cash</div>
      {cash.cash_entries.map((entry, index) => (
        <div className="details" key={index}>
          <p className="text">${entry[0]}.00</p>
          <div style={{ width: "20%" }}>
            <hr></hr>
          </div>
          <p className="text">Current: ${currentCash}.00</p>
        </div>
      ))}
    </div>
  );
};

export default Cash;
