import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { formatDate } from "../func/functions";

const TradeSum = () => {
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("weekly");

  useEffect(() => {
    const getTrades = async () => {
      try {
        const data = await fetchTrades();
        setTrades(data);
        setLoading(false);
      } catch (error) {
        setError("Error fetching trades");
        setLoading(false);
      }
    };

    getTrades();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const getTradesForPeriod = (trades, period) => {
    const today = new Date();
    if (period === "weekly") {
      const startOfWeek = new Date(
        today.setDate(today.getDate() - today.getDay())
      );
      const endOfWeek = new Date(
        today.setDate(today.getDate() + 6 - today.getDay())
      );
      return trades.filter((trade) => {
        const tradeDate = new Date(trade.entry_date);
        return tradeDate >= startOfWeek && tradeDate <= endOfWeek;
      });
    } else if (period === "monthly") {
      const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
      const endOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);
      return trades.filter((trade) => {
        const tradeDate = new Date(trade.entry_date);
        return tradeDate >= startOfMonth && tradeDate <= endOfMonth;
      });
    }
    return [];
  };

  const weeklyTrades = getTradesForPeriod(trades, "weekly");
  const monthlyTrades = getTradesForPeriod(trades, "monthly");

  const headerDate =
    view === "weekly"
      ? `Week of ${formatDate(weeklyTrades[0]?.entry_date)}`
      : `Month of ${formatDate(monthlyTrades[0]?.entry_date)}`;

  return (
    <div className="trade-summary-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
        <div className="header-date">{headerDate}</div>
      </div>

      <hr />
      <div className="trade-summary-content">
        <div className="trade-table-container">
          <table className="trade-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Pos.</th>
                <th>Price</th>
                <th>Exit</th>
                <th>RoI</th>
              </tr>
            </thead>
            <tbody>
              {(view === "weekly" ? weeklyTrades : monthlyTrades).map(
                (trade) => (
                  <tr key={trade.id}>
                    <td>{formatDate(trade.entry_date)}</td>
                    <td>
                      {trade.symbol} ${trade.strike_price} {trade.option_type}{" "}
                      {formatDate(trade.exp_date)}
                    </td>
                    <td>${trade.entry_price}</td>
                    <td>${trade.exit_price}</td>
                    <td>{(trade.roi * 100).toFixed(2)}%</td>
                  </tr>
                )
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default TradeSum;

