import React, { useEffect, useState } from "react";
import { FaChartLine, FaPercentage, FaMoneyBillWave } from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";

const DailyPNL = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  return (
    <div className="daily-pnl-container">
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {!loading && !error && (
        <div className="daily-pnl-card-container">
          {dailyPnls.map((pnl) => (
            <div key={pnl.id} className="daily-pnl-card">
              <div className="card-content">
                <FaCalendarDay className="card-icon" />
                <span className="label">Date:</span>
                <span className="value">{pnl.date}</span>
                <FaDollarSign className="card-icon" />
                <span className="label">Amount:</span>
                <span className="value">${pnl.amount}</span>
                <FaChartLine className="card-icon" />
                <span className="label">Net P/L:</span>
                <span className="value">${pnl.net_pnl}</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default DailyPNL;
