import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";
import {
  buildStyles,
  CircularProgressbarWithChildren,
} from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const { averageWin = 0, averageLoss = 0 } = analysisResults || {};

  // Calculate win and loss percentages
  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 50;
  const lossPercent = total > 0 ? (averageLoss / total) * 100 : 50;

  return (
    <div style={{ width: "100%", padding: "20px" }}>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <CircularProgressbarWithChildren
          value={50} // Keeps progress in center
          styles={buildStyles({
            pathColor: winPercent > lossPercent ? "#4caf50" : "#f44336",
            trailColor: "#d8e3ef",
            strokeLinecap: "butt",
          })}
        >
          <div style={{ display: "flex", width: "100%" }}>
            <div
              style={{
                width: `${winPercent}%`,
                height: "10px",
                backgroundColor: "#4caf50",
                borderRadius: "5px 0 0 5px",
              }}
            />
            <div
              style={{
                width: `${lossPercent}%`,
                height: "10px",
                backgroundColor: "#f44336",
                borderRadius: "0 5px 5px 0",
              }}
            />
          </div>
        </CircularProgressbarWithChildren>
      </div>
    </div>
  );
};

export default AnalysisDisplay;

