import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { FaUserCircle, FaSignOutAlt } from "react-icons/fa";
import "../styles/styles.css";

const Navigation = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [userFirstName, setUserFirstName] = useState(null);
  const [loadingUser, setLoadingUser] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem("access_token");
    if (token) {
      try {
        const decodedToken = JSON.parse(localStorage.getItem("user_data"));
        setUserFirstName(decodedToken ? decodedToken.fname : null);
      } catch (e) {
        console.error("Error decoding token", e);
      }
    }
    setLoadingUser(false); // Finished loading user data
  }, []);

  const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);
  const closeDropdown = () => setDropdownOpen(false);

  if (loadingUser) {
    return null; // Render nothing or a loading indicator while loading
  }

  return (
    <div className="navigation">
      {userFirstName ? (
        <div className="nav-container">
          <div className="search">
            <input type="text" placeholder="Search..." />
          </div>
          <div className="nav-links">
            <Link to="/" className={location.pathname === "/" ? "active" : ""}>
              Home
            </Link>
            <Link
              to="/trades"
              className={location.pathname === "/trades" ? "active" : ""}
            >
              Trades
            </Link>
            <Link
              to="/analysis"
              className={location.pathname === "/analysis" ? "active" : ""}
            >
              Analysis
            </Link>
            <div className="user-dropdown" onMouseLeave={closeDropdown}>
              <button className="user-btn" onClick={toggleDropdown}>
                <FaUserCircle className="user-icon" />
                <span>{userFirstName}</span>
              </button>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <Link
                    to="/accounts"
                    className={
                      location.pathname === "/accounts" ? "active" : ""
                    }
                  >
                    Profile
                  </Link>
                  <Link
                    to="/networth"
                    className={
                      location.pathname === "/networth" ? "active" : ""
                    }
                  >
                    Networth
                  </Link>
                  <button
                    className="logout-btn"
                    title="Logout"
                    onClick={() => {
                      localStorage.removeItem("access_token");
                      localStorage.removeItem("user_data");
                      window.location.href = "/start";
                    }}
                  >
                    <FaSignOutAlt className="logout-icon" />
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      ) : (
        <div className="nav-links">
          <Link
            to="/start"
            className={location.pathname === "/start" ? "active" : ""}
          >
            Login
          </Link>
        </div>
      )}
    </div>
  );
};

export default Navigation;

