import React, { useState } from "react";

const NewTrade = ({ onTradeSubmit }) => {
  const [formData, setFormData] = useState({
    option_symbol: "",
    option_type: "",
    strike_price: "",
    expiration_date: "",
    entry_price: "",
    contracts: "",
    underlying_stock_price: "",
    implied_volatility: "",
    rationale: "",
    feedback: "",
    outcome: "",
    trade_date: "", // Add trade_date to form data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const formatDate = (dateString) => {
    const dateParts = dateString.split("/");
    const year = dateParts[2];
    const month = dateParts[0].padStart(2, "0");
    const day = dateParts[1].padStart(2, "0");
    return `${month}/${day}/${year}`;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedExpirationDate = formatDate(formData.expiration_date);
    const tradeData = { ...formData, expiration_date: formattedExpirationDate };
    onTradeSubmit(tradeData);
    setFormData({
      option_symbol: "",
      option_type: "",
      strike_price: "",
      expiration_date: "",
      entry_price: "",
      contracts: "",
      underlying_stock_price: "",
      implied_volatility: "",
      rationale: "",
      feedback: "",
      outcome: "",
      trade_date: "",
    });
  };

  return (
    <div className="grid-item-card-right">
      <div className="card">
        <p>New Trade</p>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="option_symbol"
          placeholder="Option Symbol"
          value={formData.option_symbol}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="expiration_date"
          placeholder="Expiration Date (MM/DD/YYYY)"
          value={formData.expiration_date}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="option_type"
          placeholder="Option Type"
          value={formData.option_type}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="strike_price"
          placeholder="Strike Price"
          value={formData.strike_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="entry_price"
          placeholder="Entry Price"
          value={formData.entry_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contracts"
          placeholder="Contracts"
          value={formData.contracts}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="underlying_stock_price"
          placeholder="Underlying Stock Price"
          value={formData.underlying_stock_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="implied_volatility"
          placeholder="Implied Volatility"
          value={formData.implied_volatility}
          onChange={handleChange}
          required
        />
        <textarea
          name="rationale"
          placeholder="Rationale"
          value={formData.rationale}
          onChange={handleChange}
          required
        />
        <textarea
          name="feedback"
          placeholder="Feedback"
          value={formData.feedback}
          onChange={handleChange}
          required
        />
        <textarea
          name="outcome"
          placeholder="Outcome"
          value={formData.outcome}
          onChange={handleChange}
          required
        />
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default NewTrade;
