import React, { useState, useEffect } from "react";
import "./styles/styles.css";
import { fetchRules } from "./ApiCalls";

const Rules = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch rules from the API
  const fetchRulesData = async () => {
    try {
      const rulesData = await fetchRules();
      setRules(rulesData);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // UseEffect hook to fetch rules data when component mounts
  useEffect(() => {
    fetchRulesData();
  }, []);
  const formatDate = (dateStr) => {
    const [year, month, day] = dateStr.split("-");
    return `${year}-${month}-${day}`;
  };
  return (
    <div className="rules">
      <div className="header">Rules</div>
      {rules.map((rule, index) => (
        <div className="details" key={index}>
          <p className="text">1: {rule.rule}</p>
          <p className="text">Date: {formatDate(rule.date)}</p>
          <div style={{ width: "20%" }}>
            <hr></hr>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Rules;
