import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { fetchDailyPnls } from "../api/DailyPNLApi"; // Adjust the import based on your file structure
import { formatCash } from "../func/functions"; // Adjust the import based on your file structure

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily"); // 'daily', 'weekly', 'monthly'

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchDailyPnls();
        console.log("Fetched data:", data);
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const aggregateByWeek = () => {
    const weeklyData = {};
    dailyPNLData.forEach((entry) => {
      const date = new Date(entry.entry_date);
      const startOfWeek = new Date(
        date.setDate(date.getDate() - date.getDay())
      ); // Get the start of the week
      const weekKey = startOfWeek.toISOString().split("T")[0];

      if (!weeklyData[weekKey]) {
        weeklyData[weekKey] = {
          balance: parseFloat(entry.balance),
          entries: [],
        };
      } else {
        weeklyData[weekKey].balance += parseFloat(entry.balance);
      }
      weeklyData[weekKey].entries.push(entry);
    });

    console.log("Weekly Data:", weeklyData);
    return weeklyData;
  };

  const aggregateByMonth = () => {
    const monthlyData = {};
    dailyPNLData.forEach((entry) => {
      const date = new Date(entry.entry_date);
      const monthKey = `${date.getFullYear()}-${date.getMonth() + 1}`;

      if (!monthlyData[monthKey]) {
        monthlyData[monthKey] = {
          balance: parseFloat(entry.balance),
          entries: [],
        };
      } else {
        monthlyData[monthKey].balance += parseFloat(entry.balance);
      }
      monthlyData[monthKey].entries.push(entry);
    });

    console.log("Monthly Data:", monthlyData);
    return monthlyData;
  };

  const prepareChartData = () => {
    const labels = [];
    const dataValues = [];
    let aggregatedData = {};

    if (view === "daily") {
      aggregatedData = dailyPNLData;
      dailyPNLData.forEach((entry) => {
        labels.push(entry.entry_date);
        dataValues.push(entry.balance);
      });
    } else if (view === "weekly") {
      const weeklyData = aggregateByWeek();
      Object.keys(weeklyData).forEach((weekKey) => {
        labels.push(weekKey);
        dataValues.push(weeklyData[weekKey].balance);
      });
    } else if (view === "monthly") {
      const monthlyData = aggregateByMonth();
      Object.keys(monthlyData).forEach((monthKey) => {
        labels.push(monthKey);
        dataValues.push(monthlyData[monthKey].balance);
      });
    }

    console.log("Chart Labels:", labels);
    console.log("Chart Data:", dataValues);

    return {
      labels,
      datasets: [
        {
          label: "Balance",
          data: dataValues,
          backgroundColor: "rgba(75, 192, 192, 0.2)",
          borderColor: "rgba(75, 192, 192, 1)",
          borderWidth: 1,
        },
      ],
    };
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="bars-container">
      <div className="view-controls">
        <button onClick={() => setView("daily")}>Daily</button>
        <button onClick={() => setView("weekly")}>Weekly</button>
        <button onClick={() => setView("monthly")}>Monthly</button>
      </div>
      <Bar data={prepareChartData()} options={{ responsive: true }} />
    </div>
  );
};

export default Bars;

