// TradeForm.js

import React, { useState } from "react";
import { successModal, unsuccessModal } from "./Utility";

const TradeForm = () => {
  const [newTrade, setNewTrade] = useState({
    symbol: "",
    option_type: "",
    strike_price: 0,
    exp_date: "",
    entry_price: 0,
    exit_price: 0,
    contracts: 0,
    entry_date: "",
    close_date: "",
    // profit_loss: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTrade((prevTrade) => ({
      ...prevTrade,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/trades/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTrade),
      });
      if (response.ok) {
        successModal();
      } else {
        console.error("Failed to submit trade");
      }
    } catch (error) {
      unsuccessModal(error.message);
    }
  };

  return (
    <div className="trade-form">
      <div className="header">
        <p>New</p>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <label htmlFor="symbol">Option Symbol:</label>
        <input
          type="text"
          name="symbol"
          value={newTrade.symbol}
          onChange={handleChange}
        />
        <label htmlFor="option_type">Option Type:</label>
        <input
          type="text"
          name="option_type"
          value={newTrade.option_type}
          onChange={handleChange}
        />
        <label htmlFor="strike_price">Strike Price:</label>
        <input
          type="text"
          name="strike_price"
          value={newTrade.strike_price}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default TradeForm;
