import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const calculateBars = () => {
    let balances = [];
    let labels = [];

    if (view === "daily") {
      balances = dailyPNLData.map((day) => parseFloat(day.balance));
      labels = dailyPNLData.map((day) => day.entry_date);
      console.log("daily view - ", labels);
    } else if (view === "weekly") {
      let weekSum = 0;
      let weekLabel = "";
      dailyPNLData.forEach((day, index) => {
        weekSum += parseFloat(day.balance);
        if ((index + 1) % 5 === 0 || index === dailyPNLData.length - 1) {
          balances.push(weekSum);
          labels.push(weekLabel || day.date);
          weekSum = 0;
          weekLabel = "";
        }
        if (!weekLabel) weekLabel = day.date;
      });
      console.log("week:", weekLabel);
    } else if (view === "monthly") {
      let monthSum = 0;
      let monthLabel = "";
      dailyPNLData.forEach((day, index) => {
        monthSum += parseFloat(day.balance);
        if ((index + 1) % 20 === 0 || index === dailyPNLData.length - 1) {
          balances.push(monthSum);
          labels.push(monthLabel || day.date);
          monthSum = 0;
          monthLabel = "";
        }
        if (!monthLabel) monthLabel = day.date;
      });
    }
    console.log(labels, balances);

    return { balances, labels };
  };

  const { balances, labels } = calculateBars();
  const maxBalance = Math.max(...balances);
  const threshold = maxBalance * 1.2;

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            Daily
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
      </div>

      <hr />
      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (Math.abs(balance) / threshold) * 100;
          const isPositive = balance >= 0;
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `${percentage}%`,
                }}
              >
                {isPositive && (
                  <span className="bar-value">{formatCash(balance)}</span>
                )}
                {!isPositive && (
                  <span className="bar-value">{formatCash(balance)}</span>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;

