import React, { useState, useEffect } from "react";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  useEffect(() => {
    fetch("http://localhost:8000/watchlist/")
      .then((response) => response.json())
      .then((data) => {
        setWatchlist(data.watchlist_entries);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission for adding watchlist item
  const handleSubmit = (e) => {
    e.preventDefault();
    // Create a new watchlist item object with form data
    const newWatchlistItem = {
      symbol: formData.symbol,
      price: formData.price,
      target_price: formData.target_price,
      exp_date: formData.exp_date,
      plan: formData.plan,
    };
    // Add the new watchlist item to the watchlist
    setWatchlist([...watchlist, newWatchlistItem]);
    // Clear the form data
    setFormData({
      symbol: "",
      price: "",
      target_price: "",
      exp_date: "",
      plan: "",
    });
    // Close the modal
    setShowModal(false);
  };

  // Modal content for adding a new watchlist item
  {
    showModal && (
      <div className="form-modal">
        <div className="form-modal-content">
          <div className="header">
            <span className="close" onClick={() => setShowModal(false)}>
              &times;
            </span>
            <p>Add Watchlist Item</p>
          </div>
          <form className="new-watchlist-item-form" onSubmit={handleSubmit}>
            <label htmlFor="symbol">Symbol:</label>
            <input
              type="text"
              name="symbol"
              value={formData.symbol}
              onChange={handleChange}
            />

            <label htmlFor="price">Price:</label>
            <input
              type="text"
              name="price"
              value={formData.price}
              onChange={handleChange}
            />

            <label htmlFor="target_price">Target Price:</label>
            <input
              type="text"
              name="target_price"
              value={formData.target_price}
              onChange={handleChange}
            />

            <label htmlFor="exp_date">Exp. Date:</label>
            <input
              type="date"
              name="exp_date"
              value={formData.exp_date}
              onChange={handleChange}
            />

            <label htmlFor="plan">Plan:</label>
            <textarea
              name="plan"
              value={formData.plan}
              onChange={handleChange}
            ></textarea>

            <button type="submit">Add Watchlist Item</button>
          </form>
        </div>
      </div>
    );
  }

  {
    showEditModal && selectedWatch && (
      <div className="form-modal">{/* Edit form modal content here */}</div>
    );
  }
};

export default Watchlist;
