// TradeForm.js

import React, { useState } from "react";
import { successModal, unsuccessModal } from "./Utility";

const TradeForm = () => {
  const [showModal, setShowModal] = useState(false);

  const [newTrade, setNewTrade] = useState({
    symbol: "",
    option_type: "",
    strike_price: 0,
    exp_date: "",
    entry_price: 0,
    exit_price: 0,
    contracts: 0,
    entry_date: "",
    close_date: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTrade((prevTrade) => ({
      ...prevTrade,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/trades/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTrade),
      });
      if (response.ok) {
        successModal();
      } else {
        console.error("Failed to submit trade");
      }
    } catch (error) {
      unsuccessModal(error.message);
    }
  };

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="trade-form">
      <button onClick={openModal}>Open Form Modal</button>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <div className="header-card">
              <p>New Trade</p>
            </div>
            <form className="new-trade-form" onSubmit={handleSubmit}>
              {/* Your form inputs here */}
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TradeForm;
