import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";
import { ProgressBar } from "react-bootstrap";
import { FaInfoCircle } from "react-icons/fa";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  const formatValue = (value, isCurrency = false) => {
    if (isNaN(value) || value === undefined) {
      return isCurrency ? "$0.00" : "N/A";
    }
    return isCurrency ? `$${value.toFixed(2)}` : `${value.toFixed(2)}`;
  };

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const {
    absoluteReturn = 0,
    // other fields...
  } = analysisResults || {};

  const absoluteReturnPercentage = (absoluteReturn / 80) * 100; // Calculate percentage towards 80% goal

  return (
    <div className="analysis-container">
      {/* Absolute Return Metric with Straight Progress Bar */}
      <div className="analysis-metric">
        <h3>
          Absolute Return <FaInfoCircle />
        </h3>
        <p>{formatValue(absoluteReturn, true)}</p>

        {/* Progress Bar */}
        <ProgressBar
          now={absoluteReturnPercentage} // fills based on progress towards 80%
          label={`${absoluteReturn.toFixed(2)}%`} // shows the actual percentage on the bar
          variant={absoluteReturn >= 80 ? "success" : "warning"} // green if target met, yellow otherwise
        />
      </div>
    </div>
  );
};

export default AnalysisDisplay;

