import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import { fetchDailyPnls } from "../api/DailyPNLApi";

const Calender = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [modalDetails, setModalDetails] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  const tileContent = ({ date, view }) => {
    if (view === "month") {
      const pnlEntry = dailyPNLData.find((pnl) => {
        const entryDate = new Date(pnl.entry_date + 1);
        return (
          date.getFullYear() === entryDate.getFullYear() &&
          date.getMonth() === entryDate.getMonth() &&
          date.getDate() === entryDate.getDate()
        );
      });

      return pnlEntry ? (
        <div className="pnl-tile">
          <div>${pnlEntry.balance}</div>
          <div>{pnlEntry.roi}%</div>
        </div>
      ) : null;
    }
  };

  const handleTileClick = (date) => {
    const pnlEntry = dailyPNLData.find((pnl) => {
      const entryDate = new Date(pnl.entry_date + 1);
      return (
        date.getFullYear() === entryDate.getFullYear() &&
        date.getMonth() === entryDate.getMonth() &&
        date.getDate() === entryDate.getDate()
      );
    });
    if (pnlEntry) {
      setModalDetails({
        date: date.toDateString(),
        ...pnlEntry,
      });
    }
  };

  const handleCloseModal = () => {
    setModalDetails(null);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="calendar-container">
      <Calendar
        onChange={setSelectedDate}
        value={selectedDate}
        tileContent={tileContent}
        onClickDay={handleTileClick}
      />
      {modalDetails && (
        <div className="modal-container">
          <div className="modal-content">
            <div className="modal-header">
              <h2>PNL Details for {modalDetails.date}</h2>
              <button className="close-button" onClick={handleCloseModal}>
                &times;
              </button>
            </div>
            <div className="modal-body">
              <p>
                <strong>Balance:</strong> ${modalDetails.balance}
              </p>
              <p>
                <strong>RoI:</strong> {modalDetails.roi}%
              </p>
              <p>
                <strong>Close Cash:</strong> ${modalDetails.close_cash}
              </p>
              <p>
                <strong>Open Cash:</strong> ${modalDetails.open_cash}
              </p>
              {/* Add more details if necessary */}
            </div>
          </div>
        </div>
      )}
      {dailyPNLData.length === 0 && <p>No PNL data available</p>}
    </div>
  );
};

export default Calender;
