import React, { useState } from "react";
import "./styles/newwatch.css";

const NewWatch = ({ onAdd }) => {
  const [showModal, setShowModal] = useState(false);

  const [formData, setFormData] = useState({
    enter_date: "",
    symbol: "",
    current_price: "",
    target_price: "",
    expiration_date: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log("Submitting payload:", formData);

      // Make API request to add the new watchlist item
      const response = await fetch("http://localhost:8000/watchlist", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error("Failed to add watchlist item");
      }

      const data = await response.json();
      onAdd(data);

      // Reset the form data
      setFormData({
        enter_date: "",
        symbol: "",
        current_price: "",
        target_price: "",
        expiration_date: "",
      });

      // Close the modal after successful submission
      setShowModal(false);
    } catch (error) {
      console.error("Error adding watchlist item:", error);
    }
  };

  return (
    <div className="new-watch">
      <div className="header-card">
        <p>New</p>
        {/* <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i> */}
      </div>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>
              &times;
            </span>
            <div className="card">
              <h1>Add New Watchlist Item</h1>
            </div>
            <form className="new-watch-form" onSubmit={handleSubmit}>
              <label htmlFor="enter_date">Date:</label>
              <input
                type="date"
                name="enter_date"
                value={formData.enter_date}
                onChange={handleChange}
                required
              />
              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
                required
              />
              <label htmlFor="current_price">Current Price:</label>
              <input
                type="text"
                name="current_price"
                value={formData.current_price}
                onChange={handleChange}
                required
              />
              <label htmlFor="target_price">Target Price:</label>
              <input
                type="text"
                name="target_price"
                value={formData.target_price}
                onChange={handleChange}
                required
              />
              <label htmlFor="expiration_date">Expiration Date:</label>
              <input
                type="date"
                name="expiration_date"
                value={formData.expiration_date}
                onChange={handleChange}
                required
              />
              <input type="submit" value="Add" />
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewWatch;
